}
vec3 pal(float t){return .5+.5*cos(6.28*(1.*t+vec3(.1,.3,.4)));}
float box(vec3 p,vec3 b){
    vec3 q = abs(p)-b;
    return length(max(q,vec3(0.)))+min(0.,max(q.x,max(q.y,q.z)));
}
float sdCappedCylinder(vec3 p, vec3 a, vec3 b, float r)
{
  vec3  ba = b - a;
  vec3  pa = p - a;
  float baba = dot(ba,ba);
  float paba = dot(pa,ba);
  float x = length(pa*baba-ba*paba) - r*baba;
  float y = abs(paba-baba*0.5)-baba*0.5;
  float x2 = x*x;
  float y2 = y*y*baba;
  float d = (max(x,y)<0.0)?-min(x2,y2):(((x>0.0)?x2:0.0)+((y>0.0)?y2:0.0));
  return sign(d)*sqrt(abs(d))/baba;
}
float yd =0.;
float xd=0.;
float id = 0.;
vec2 sdf(vec3 p){

 float time = iTime*150./60.*.5;
 p.z +=(floor(time)+pow(fract(time),4.));
  p.z *=.25;
    id = floor(p.z);
     p.xy *=rot(id+sign(mod(id,2.)-.5)*iTime*.2);
      p.x  +=asin(sin(time*.5+id*.2))*.5;
    p.y  +=asin(cos(time*.5+id*.2))*.5;

    p.z =   fract(p.z)-.5;
    p.z /=.25;
    vec2 h; 
    h.x = box(p,vec3(1.1));
    h.y = mod(id,3.)+1.;



    vec2 t ;
    vec3 tp = p;
    tp = abs(tp);
    tp.y -= 2.+ (yd=sin(iTime+id))*.75;
    t.x = box(tp,vec3(1.,.125,1.));
    t.y = 4.;
    h = t.x < h.x ? t:h;

    for(float i=0.;i<2.;i++){
        tp.xz =abs(tp.xz)-.155;
        tp.xz *=rot(.785+id);
    }
    t.x = sdCappedCylinder(tp,-vec3(0.+cos(id)*.1,2.,0),-vec3(0.,tp.y,0.),.2+sin(iTime+p.y)*.1);
    t.y = 2.;

    h = t.x < h.x ? t:h;

    tp = p;
    tp = abs(tp);
    tp.x -= 2.+(xd=cos(iTime-id))*.75;
    t.x = box(tp,vec3(.125,1.,1.));
    t.y = 4.;
    h = t.x < h.x ? t:h;
    for(float i=0.;i<2.;i++){
        tp.yz =abs(tp.yz)-.155;
        tp.yz *=rot(.785+id);
    }
    t.x = sdCappedCylinder(tp,-vec3(2.,0.,0),-vec3(tp.x,0.,0.),.2+sin(iTime+tp.x)*.1);
