//inspired from: https://www.shadertoy.com/view/XllGRl

#define NUMBER_STROKES 500
#define MULT 2


//////////////////////////////////////////////////////////////////////////////////////
/* brush                                                                            */
//////////////////////////////////////////////////////////////////////////////////////

//Random

uint seed = 2647417u;
uint seed3;

void hash(){
    seed ^= 2747636419u;
    seed *= 2654435769u;
    seed ^= seed >> 16;
    seed *= 2654435769u;
    seed ^= seed >> 16;
    seed *= 2654435769u;
}

float random(){
    hash();
    return float(seed)/4294967295.0;
}

void hash2(){
    seed3 ^= 2747636419u;
    seed3 *= 2654435769u;
    seed3 ^= seed3 >> 16;
    seed3 *= 2654435769u;
    seed3 ^= seed3 >> 16;
    seed3 *= 2654435769u;
}

float random2(){
    hash2();
    return float(seed3)/4294967295.0;
}

float noise(vec2 x){
    vec2 f = fract(x);
    vec2 u = f*f*(3.0 - 2.0*f);

    vec2 p = floor(x);
	float a = texture(iChannel0, (p+vec2(0.0, 0.0))/256.0).x;
	float b = texture(iChannel0, (p+vec2(1.0,0.0))/256.0).x;
	float c = texture(iChannel0, (p+vec2(0.0,1.0))/256.0).x;
	float d = texture(iChannel0, (p+vec2(1.0,1.0))/256.0).x;


	return a+(b-a)*u.x+(c-a)*u.y+(a-b-c+d)*u.x*u.y;
}

vec2 seed2 = vec2(0.0);
float fbm(vec2 x, int detail){
    float a = 0.0;
    float b = 1.0;
    float t = 0.0;
    x += seed2;
    for(int i = 0; i < detail; i++){
        float n = noise(x);
        a += b*n;
        t += b;
        b *= 0.7;
        x *= 2.0; 

    }
    return a/t;
}

#define PI 3.14159265359



vec2 mn = vec2(0.0, 0.0);
vec3 random_sphere(){
    const float a = 4.0*PI/float(NUMBER_STROKES);
    const float d = sqrt(a);
    const float M1 = round(PI/d);
    const float d1 = PI/M1;
    const float d2 = a/d1;

    float teta = PI*(mn.x + 0.5)/M1;
    float M2 = round(2.0*PI*sin(teta)/d2);

    float phi = 2.0*PI*mn.y/M2;

    mn.y += 1.0;
    if (mn.y > M2){
        mn.x += 1.0;
        mn.y = 0.0;
    } 

    teta += random() * 0.2 - 0.1;
    phi += random() * 0.2 - 0.1;
    return vec3(sin(teta)*cos(phi), cos(teta), sin(teta)*sin(phi));

}

vec3 random_sphere2(){
    float teta = random()*2.0*PI;
    float z = random()*2.0-1.0;
    float s = sqrt(1.0-z*z);
    return vec3(s*cos(teta), s*sin(teta), z);
}
//aux///////////

vec3 colorRamp(vec3 col1, vec3 col2, vec3 col3, float t){
    if (t < 0.5){
        t*=2.0;
        t = t*t*(3.0 - 2.0*t);
        return mix(col1, col2, t);
    }else{
        t = (t-0.5)*2.0;
        t = t*t*(3.0 - 2.0*t);
        return mix(col2, col3, t);
    }
}

//brush

vec4 brush(vec2 uv){ //color && alpha
    uv.y += 0.4*(fbm(vec2(uv.x*2.0, 0.5), 2) - 0.5);
    //color
    float t = 0.3 + 0.4*fbm(uv*vec2(10.0, 60.0) + 10.0, 3);
    t += 0.6*fbm(uv*2.0+20.0, 2)-0.3;
    t += 0.7*noise(seed2)-0.35;

    //strength
    float b = 1.0;
    b *= pow(fbm(uv*vec2(1.0, 3.0), 8), 2.0);
    b *= smoothstep(0.9, 0.5, 2.0*max(abs(uv.x - 0.5), abs(uv.y - 0.5)));
    b = smoothstep(0.17, 0.3, b);

    return vec4(colorRamp(vec3(0.404,0.133,0.357), 
                     vec3(1.000,0.690,0.259),
                     vec3(0.753,0.165,0.165), t), b);
}

int rot_idx = 0;
vec4 brush2(vec2 uv){ //color && alpha
    uv.y += 0.4*(fbm(vec2(uv.x*2.0, 0.5), 2) - 0.5);
    //color
    float t = 0.3 + 0.4*fbm(uv*vec2(10.0, 60.0) + 10.0, 3);
    t += 0.6*fbm(uv*2.0+20.0, 2)-0.3;
    t += 0.7*noise(seed2)-0.35;

    //strength
    float b = 1.0;
    b *= pow(fbm(uv*vec2(1.0, 3.0), 8), 2.0);
    b *= smoothstep(0.9, 0.5, 2.0*max(abs(uv.x - 0.5), abs(uv.y - 0.5)));
    b = smoothstep(0.27, 0.3, b);

    vec3 colors[3] = vec3[3](
        vec3(0.769,0.769,0.769),
        vec3(0.188,0.169,0.153),
        vec3(0.263,0.259,0.259)
    );
    rot_idx += 1;
    rot_idx %= 3;
    return vec4(colorRamp(vec3(0.204,0.157,0.114), 
                     colors[rot_idx],
                     vec3(0.239,0.173,0.141), t), b);
}

//////////////////////////////////////////////////////////////////////////////////////
/* rtx                                                                              */
//////////////////////////////////////////////////////////////////////////////////////

vec4 intersect(vec3 ro, vec3 rd, vec3 p, inout vec3 n){
    float t = dot(p - ro, n)/dot(rd, n);

    if (t < 0.0) return vec4(-1.0);

    vec3 up = vec3(0.0, 1.0, 0.0);
    if (dot(n, up) > 0.95) up = vec3(0.7071, 0.0, 0.7071);

    vec3 u = normalize(cross(up, n));
    vec3 v = cross(u, n);

    vec3 p2 = ro + rd*t;
    vec2 uv;

    uv.x = dot(u, p2 - p);
    uv.y = dot(v, p2 - p);
    uv *= vec2(7.0, 10.0); //size
    if(abs(uv.x) > 1.0 || abs(uv.y) > 1.0) return vec4(-1.0);

    uv = uv*0.5+0.5;
    vec4 b = brush(uv);

    if (b.w < random2()) return vec4(-1.0);

    n = normalize(n + 0.5*v*(fbm(vec2(20.0, 60.0)*uv, 3) - 0.5));

    return vec4(t, b.rgb);
}


vec3 scene(vec3 ro, vec3 rd){
    float t = 1e10;
    vec3 col = vec3(0.000,0.000,0.000);

    for(int i = 0; i < NUMBER_STROKES; i++){
       vec3 pos = random_sphere();
       vec3 n = normalize(pos + 0.5*vec3(random(), random(), random()) - 0.25);
       seed2 = vec2(random(), random()) * 256.0;
       pos *= 0.5;

       if (dot(-ro, pos) > 0.0) continue;

       vec4 inter = intersect(ro, rd, pos, n);
       if (inter.x > 0.0 && inter.x < t){
           t = inter.x;
           col = 0.5*vec3(0.059,0.102,0.122);
           //light1
           const vec3 l1 = normalize(vec3(1.0, 1.0, 0.0)); 
           const vec3 c1 = 2.0*vec3(1.000,0.812,0.302);
           float dNL1 = dot(n, l1);
           col += inter.yzw * c1 * max(0.0, dNL1);
           if (dNL1 > 0.0)
               col += c1 * pow(max(0.0, dot(reflect(normalize(pos - ro), n), l1)), 10.0);


           //light2
           const vec3 l2 = normalize(vec3(-1.0, 0.0, 0.2)); 
           const vec3 c2 = vec3(0.004,0.255,0.145);
           col += inter.yzw * c2 * pow(max(0.0, dot(n, l2)), 4.0);


       }
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    seed3 = uint(fragCoord.y*iResolution.x + fragCoord.x) + uint(iFrame)*uint(iResolution.x)*uint(iResolution.y);
    vec2 uv = fragCoord/iResolution.xy*2.0-1.0;
    uv.x *= iResolution.x/iResolution.y;



    vec3 ro = vec3(sin(iTime*0.5), 0.5, cos(iTime*0.5));

    vec3 dir0 = normalize(-ro);
    vec3 up = vec3(0.0, 1.0, 0.0);
    vec3 right = normalize(cross(up, dir0));
    up = cross(dir0, right);
    vec3 rd = normalize(dir0 + right*uv.x + up*uv.y);

    vec3 col = vec3(0.0);
    if (length(uv) < 0.6){
    for(int i = 0; i < MULT; i++)
        col += scene(ro, rd);
    col /= float(MULT);
    }

    col = mix(col, texture(iChannel1, fragCoord/iResolution.xy).rgb, 0.3);



    fragColor = vec4(col, 1.0);
}

