float getEdge(vec2 fragCoord, float th)
{

    vec2 p = fragCoord.xy;

    vec2 uv = (p.xy) / iResolution.xy;


	// simple sobel edge detection
    float gxr = texture(iChannel0, uv.xy+vec2(th, 0.)).w
    - texture(iChannel0, uv.xy-vec2(th, 0.)).w;

    float gyr = texture(iChannel0, uv.xy+vec2(0., th)).w
    - texture(iChannel0, uv.xy-vec2(0., th)).w;


	// hack: use g^2 to conceal noise in the video
    float gr = gxr*gxr + gyr*gyr;

    float g = pow(gr,9.99);
    return clamp(g*1.,0.,1.);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(1.);

    //col = col1;
    col = clamp(texture(iChannel0,uv).rgb,0.,1.);
   // col = vec3(t);
    col = mix(col, vec3(0.8,0.6,0.1)*1.,vec3(getEdge(fragCoord,0.0025)));
    col = mix(col, vec3(0.0,0.3,0.8)*1.,vec3(getEdge(fragCoord, 0.0009)));

   //col = s40;
   // col *= clamp(texture(iChannel1,uv*2.).rgb,0.,1.).x*1.2;

    //vignette
    uv = fragCoord/iResolution.xy;
    uv *=  1.0 - uv.yx;
    float vig = uv.x*uv.y * 25.0; // multiply with sth for intensity

    vig = pow(vig, 0.5); // change pow for modifying the extend of the  vignette

    fragColor = vec4(col*vig*1.2,1.0);
}

