#define TEXTURE_STEP 1./6.
#define EPS 0.001
#define FAR 40.

mat2 rot(float a){
    float s = sin(a), c = cos(a);
    return mat2(c,-s,s,-c);
}
float roundedBox(vec3 sp, vec3 dim, float r){
    sp = abs(sp) - dim;
    return min(max(sp.x, max(sp.y, sp.z)), 0.0) 
    + length(max(sp, 0.0)) - r;    


}
float map(vec3 pos){
    float d = 1000.;
    vec3 flpos = floor((pos)/6.);
    float rf = hash13(flpos);
    pos = mod(pos,6.)-3.;

    if(hash13(flpos+100.)<0.5){
    d = min(d, length(pos)-2.3+rf*0.4);}
    else{

    d = min(d, roundedBox(pos, vec3(.5), 1.+rf*0.7));
    }
    //pos.xy *= rot(iTime);
    //d = min(d, roundedBox(pos, vec3(1.), 0.1));
    return d;
}

float trace(vec3 ro, vec3 rd){
    float d,t=0.;
    for(float i = 0.; i < 96.; i++){
        d = map(ro + rd*t);

        if(abs(d) < EPS || t > FAR){
            break;
        }

        t += d*0.95;
    }
    return t;
}

vec3 getNormal(vec3 p){
    vec2 e = vec2(EPS, 0.);
    float dx = map(p - e.xyy) - map(p + e.xyy);
    float dy = map(p - e.yxy) - map(p + e.yxy);
    float dz = map(p - e.yyx) - map(p + e.yyx);
    return normalize(vec3(dx,dy,dz));
}

vec3 getCameraDirection(vec2 uv, vec3 ro, vec3 ta){

    vec3 fwd = normalize(ta-ro);
    vec3 uu = vec3(0.,1.,0.);
    vec3 ri = cross(uu,fwd);
    vec3 up = cross(fwd,ri);
    return normalize(uv.x*ri + uv.y*up + fwd*1.);
}

vec2 render(vec2 fragCoord, inout vec3 lightColor)
{

    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y;
    vec3 col =vec3(0.);
    vec3 ta = vec3(0.,0.,iTime*0.5);
    vec3 ro = ta +  vec3(0.,0.,-2.);
    if(iMouse.z > 0.5){
        float rotScale = 4.;
        ro.xz *= rot(8.*(iMouse.x/iResolution.x)-rotScale/2.);
       // ro.yz *= rot(4.*(iMouse.y/iResolution.y)-rotScale/2.);
    }
    else{
         //ro.xz *= rot(iTime*0.15);
    }
    vec3 lp1 = ro + vec3(50.,10.,1.);
    vec3 lp2 = ro +  vec3(-50.,7.,2.);

    vec3 rd = getCameraDirection(uv,ro,ta);
    float cs = cos(iTime*0.25);
    float si = sin(iTime*0.25); 
    rd.xy*= mat2(cs, si, -si, cs);
    rd.xz*= mat2(cs, si, -si, cs);
    float t = trace(ro, rd);
    float diff1 = 0., diff2 = 0., totalLight;
    if(t < FAR){
        vec3 pos = ro + rd*t;
        vec3 flpos = floor((pos)/6.);
        vec3 cc = hash33(flpos);
        vec3 n = getNormal(pos);
        diff1 = max(0.,dot(n,normalize(lp1-pos)));
        lightColor = diff1*vec3(0.7,0.3,0.1)*2.;
        diff2 =  max(0.,dot(n,normalize(lp2-pos)));
        lightColor = mix(lightColor, vec3(0.2,0.8,0.8), diff2)*2.;
        lightColor = lightColor*0.8 + 0.2*(hash13(flpos+8410.) < 0.5 ? 
                                       vec3(0.7,0.3,0.1) : 
                                       vec3(0.2,0.8,0.8));
        lightColor += cc*0.25;
        totalLight = diff1+diff2;
        float stripe_Payments = smoothstep(0.23,0.25,abs(fract(pos.y/2./hash13(flpos))-0.5));
        lightColor -= stripe_Payments*0.125;
    }
    else{
        t = FAR;
    }



    return vec2(totalLight*0.5,t);
}


