#define TMIN 0.01
#define TMAX 200.
#define RAYMARCH_TIME 128
#define PRECISION .001
#define AA 3
#define PI 3.1415926

#define S(v,r) smoothstep( r, r+ 3./iResolution.y, v )
float logo(vec2 uv);

//========SDFunctions========
float sdSphere(vec3 p, vec3 o, float r){
    return length(p-o)-r;
}
float sdCapsule(vec3 p,vec3 a, vec3 b,float r){
    vec3 ab = b - a;
    vec3 ap = p - a;
    float t = dot(ab, ap)/dot(ab,ab);
    t = clamp(t,0.,1.);
    vec3 c = a +t*ab;
    return length(p-c)-r;
}
//https:iquilezles.org/articles/distfunctions
float sdRoundCone( vec3 p, float r1, float r2, float h )
{
  // sampling independent computations (only depend on shape)
  float b = (r1-r2)/h;
  float a = sqrt(1.0-b*b);

  // sampling dependant computations
  vec2 q = vec2( length(p.xz), p.y );
  float k = dot(q,vec2(-b,a));
  if( k<0.0 ) return length(q) - r1;
  if( k>a*h ) return length(q-vec2(0.0,h)) - r2;
  return dot(q, vec2(a,b) ) - r1;
}
//===============TRANSFORM=================
mat2 rotate(float a){
return mat2(cos(a),sin(a),-sin(a),cos(a));
}
float smUni( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}
//===============RENDER===================
//Scene
float f(vec3 p){
    p.x=abs(p.x);
    float d;
    {//face
    vec3 p0 = vec3(p.x*.8,p.y+.4,p.z);
    d = sdSphere(p0,vec3(0),1.5); 
    p0 = vec3(p.x*.6,p.y+.6,p.z+.7);
    float d0 = sdSphere(p0,vec3(0),1.);
    d = smUni(d,d0,.3);
    }
    float angle = sin(iTime*5.)*.1;
    {//ear
    vec3 p0 = vec3(p.x-.85,p.y-.3,p.z*1.2+.3);
    p0.xy *= rotate(-.3);
    p0.yz *= rotate(-.3);
    float d0 = sdRoundCone(p0,.8,.2,1.2)/1.2; 
    d = smUni(d,d0,.3);
    }
    {//nose
    float d0 = sdCapsule(p,vec3(0,0,-1.5),vec3(0,-.6,-1.7),.3); 
    d = smUni(d,d0,.05);
    }
    {//nose
    float d0 = sdSphere(p,vec3(.3,-.6,-1.65),.45); 
    d = smUni(d,d0,.05);
    }
    {//eye
    vec3 p0 = vec3(p.x-.6,p.y+.1,p.z+1.45);
    p0.xy *= rotate(-1.2);
    p0.yz *= rotate(-0.3);
    float d0 = sdRoundCone(p0,.06,.08,.2); 
    d = smUni(d,d0,.05);
    }
    {//
    vec3 p0 = vec3(p.x-.9,p.y+.5,p.z+1.3);
    p0.xy *= rotate(.2+angle*.5);
    float d0 = sdCapsule(p0,vec3(0,0,0),vec3(.8,0,0),.1);
    p0.xy *= rotate(-.4+angle*.5);
    d0 = smUni(d0,sdCapsule(p0,vec3(0,0,0),vec3(.8,0,0),.1),.05);
    d = smUni(d,d0,.05);
    }
    return d;
}
float rayMarch(in vec3 ro, in vec3 rd) {
    float t = TMIN;
    for(int i = 0; i < RAYMARCH_TIME ; i++) {
        vec3 p = ro + t * rd;
        float d = f(p);
        t += d;
        if(d < PRECISION || t > TMAX)
            break;
    }
    return t;
}
// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal(in vec3 p) {
    const float h = 0.0001;
    const vec2 k = vec2(1, -1);
    return normalize(k.xyy * f(p + k.xyy * h) +
        k.yyx * f(p + k.yyx * h) +
        k.yxy * f(p + k.yxy * h) +
        k.xxx * f(p + k.xxx * h));
}

mat3 setCamera(in vec3 camtar, in vec3 campos, in float camro){
    vec3 z = normalize(camtar-campos);
    vec3 cp = vec3(sin(camro),cos(camro),0.);
    vec3 x = normalize(cross(cp,z));
    vec3 y = cross(z,x);
    return mat3(x,y,z);
}
vec3 render(vec2 uv){
    vec3 lightPos = vec3(-5., 5.,-5);//light

    //SET Camera
    vec3 cam_tar = vec3(0,-.5,0);//cam target
    float angle = pow(sin(iTime+.3),3.)*.5;
    vec3 cam_pos = vec3(sin(angle),0,-cos(angle))*10.;//cam position

    vec3 rd = vec3(uv,4.); //decide view width
    if(iMouse.x!=0. || iMouse.y!=0. ){
        float angle_x = -(iMouse.x /iResolution.x-0.5)  *0.5*PI;
        float angle_y = (iMouse.y /iResolution.y-0.5)  *0.5*PI;
        rd.yz *= rotate(angle_y);
        rd.zx *= rotate(angle_x);}
    rd = normalize(setCamera(cam_tar,cam_pos,0.)*rd);//viewing frustum

    float t = rayMarch(cam_pos,rd);//raymarching

    vec3 color = mix(vec3(0,0,1),vec3(0,0,.6),uv.y*.5+.5);//background

    if(t > TMAX) return color; 

    vec3 p = cam_pos + t*rd;
    vec3 n = calcNormal(p);
    vec3 l = normalize(lightPos-p);//lightDir
    vec3 h = normalize(l-rd);//

    vec3 diffusecol = vec3(1.);//diffuse color
    vec3 specol = vec3(1.);//specular color
    vec3 cmrfl = .5*vec3(1)*textureLod(iChannel0,reflect(rd,n),1.).rgb;//cubemap reflection

    float dif = clamp(dot(l,n),0.,1.);//diffuse    
    float spe = pow(clamp(dot(h,n),0.,1.),50.);//specular
    float fresnel = pow(clamp(1. - dot(n,-rd),0.,1.),5.);

    color = cmrfl + .1*dif*diffusecol + smoothstep(.6,1.,spe)*specol;//metal
    color = smoothstep(0.,1.,color);//add contrast

    color += fresnel*.4;

    return color;
}
vec2 getuv(vec2 coord){
    return (2.*coord-iResolution.xy)/iResolution.y;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec3 color = vec3(0.);
    #if AA>1
    for(int m = 0; m < AA; m++) {
        for(int n = 0; n < AA; n++) {
            vec2 offset = 2. * (vec2(float(m), float(n)) / float(AA) - .5);
            vec2 uv = getuv(fragCoord + offset);
    #else
            uv = getuv(fragCoord);
    #endif
            color += render(uv);
    #if AA>1
        }
    }
    color /= float(AA*AA);
    #endif


    color = mix(color,vec3(1),logo(uv));
    fragColor = vec4(color,1.0);
}


float logo(vec2 uv){
float n = 0.;
n += S(.05,abs(length(uv-vec2(-1.23,0))-.12));
n *= 1.-S(-1.25,uv.x);
n += S(.17,abs(uv.y))*S(.05,abs(uv.x+1.29));
n += S(.05,abs(length(uv-vec2(-1.5,0))-.12));
n += S(.05,abs(length(uv-vec2(-0.9,0))-.12));
n += (1.+S(-.6,uv.x)-S(.05,uv.y))*S(.05,abs(length(uv-vec2(-0.6,.03))-.09));
n += (1.-S(-.6,uv.x)+S(-.05,uv.y))*S(.05,abs(length(uv-vec2(-0.6,-.04))-.09));
return n;
}

