float shadow(vec3 p, vec3 l, float maxdist, int stepcount, float limit) {
  float shad=1.0;
  float dd=0.0;
  for(int i=0; i<stepcount; ++i) {
    float d=min(map(p), maxdist);
    if(d<limit) {
      shad=0.0;
      break;
    }
    if(dd>=maxdist) {
      break;
    }

    p += l*d;
    dd+= d;

  }
  return shad;
}




float intersect(vec3 p, vec3 l, float maxdist, int stepcount, float limit) {
  float shad=1.0;
  float dd=0.0;
  for(int i=0; i<stepcount; ++i) {
    float d=min(map(p), maxdist);
    if(d<limit) {
      shad = 0.;
      break;

    } 

    if(dd>=maxdist) {
      break;
    }

    p += l*d;
    dd+= d;

  }
  return shad;
}


vec3 vollight ( vec3 lightpos, vec3 s, vec3 r, vec2 uv, float limit) {

  float rand=rnd(uv);


  const int volsteps=200; //50
  float voldist = 25.0;
  float stepsize = voldist/float(volsteps);
  vec3 lr=r*stepsize;
  vec3 lp=s + lr*rand ;
  float stepdist=rand*stepsize;
  vec3 atcol=vec3(0);

  for(int i=0; i<volsteps; ++i) {

    if(stepdist>limit) {
      break;
    }

    vec3 lv = lightpos-lp;
    float ldistvol = length(lv);
    lv = normalize(lv);
    float shadvol = intersect(lp, lv, ldistvol, 10, 0.01);
    atcol += .1/(0.01+(pow(ldistvol,1.))) * shadvol;


    lp+=lr;
    stepdist+=stepsize;

  }
  return atcol;
}

vec3 tune ( float t) {
  vec3 p = vec3(0);

  float n = 1.;

  for ( float i=0.; i < 5.; i++) {
      p.x += sin(t * n)/n;
      p.y += sin((t + 11.)* n)/n;

      n *= 1.4;


  }

  return p;

}

