void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);


  float tt = iTime * 1.;
  vec3 jump = vec3(1) * tick(iTime*.1)*14.;

  vec3 s = vec3(10.,3.2,7.1)*iTime*.08 + jump;

  vec3 arm = vec3(17,0,0);
  arm.xz *= rot(sin(iTime* .19));
  arm.yz *= rot(sin(iTime*.23));
  arm.yx *= rot(sin(iTime*.28));

  vec3 t = s + arm;


  vec3 tensor = vec3(6);

  tensor *= sin(tt);


  tensor.xz *= rot(tt);
  tensor.yz *= rot(tt * .83);
  tensor.xy *= rot(tt * .71);


  tensor += t;


  vec3 probe = tensor;







  vec3 cz=normalize(t-s);
  vec3 cx=normalize(cross(cz,vec3(0,-1,0)));
  vec3 cy=normalize(cross(cz,cx));
  vec3 r=normalize(cx*uv.x+cy*uv.y+cz*0.7);





  // march

  vec3 p=s;
  float dd=0.0;
  const float maxdist = 1000.0;
  float z;
  bool hit = false;
  for( z=0.; z<1000.; ++z) {

    float d=map(p);
    if(d<0.01) { hit = true; break;}
    if(dd>maxdist) { dd=maxdist; break;}
    p+=r*d;
    dd+=d;

  }

  vec3 hue = vec3(.1,.1,.2);

  vec3 col = hue;
  //float ao = pow(1. - z/500.,1.);
  //col *= ao;


  vec3 light = normalize(vec3(1));
  vec3 n = norm(p);

  float spec =pow(max(dot(reflect(-light,n),-r),0.),40.) * 10.;
  col += spec * .1;
  float diff = max(0., dot(n,light)*.5 +.5);
  col *= diff;


  vec3 n2 = norm(p, vec2(0.0, 1E-2 ));// + 3E-2*.01) );
  vec3 n1 = norm(p, vec2(0.0, 2.03E-2) );
  #define saturate(a) (clamp((a),0.,1.))
 float edge = saturate(length(n1-n2)/0.1);
  if ( edge > 0.01) {
      col = vec3(0);
  }



  if (! hit){
      col = vec3(0);

  }


  vec3 vcol = vollight(probe, s , r, uv, dd);
  col += vcol*.53;




  fragColor = vec4(col, 1);
}
