vec3 spline(vec3 a, vec3 b, vec3 c, vec3 d, float p)
{
    // CatmullRoms are cardinals with a tension of 0.5
	vec3 P = -a + (3. * (b - c)) + d;
    vec3 Q = (2. * a) - (5. * b) + (4. * c) - d;
    vec3 R = c - a;
    vec3 S = 2. * b;

	float p2 = p * p;
    float p3 = p * p2;

    return .5 * ((P * p3) + (Q * p2) + (R * p) + S);
}

