      vec3(3.6,3.0+0.4*phase,3.1),
      vec3(3.0,2.3,3.5)
  );

  pt=mod(pt-.5,1.)-.5;


  vec3 seed = floor(p/width);
  float uu = hash13(seed);
  float dist;


  dist=box(pt,vec3(.19));


  dist=max(dist,clampBox);


  return dist;

}
