float lane(vec3 p, vec3 dim, float domain, float prob) {




    float tt = iTime * .3;
    vec3 q = p;

    // wierd skylights
    q.yz *= rot(.42);
    q.xz *= rot(.4);

    q.xy *= rot(q.z/50.);
    q.x += tt * 10.;
    q.xz = mod( q.xz, 40.) - 20.;

