        float u = box(p,dim);

        return u;
    }

}

float highway(vec3 p){


    float tt = iTime * 0.1;

    vec3 ap = p  + tt * 45.1 * vec3(0,0,1);
    vec3 bp = p  + tt * 6.1 * vec3(0,0,1);
    vec3 cp = p  + tt * 1.1 * vec3(0,0,1);

    float u = 1.;
    float a = lane(ap, vec3(.6)*u*1.4,       2.4*u, .4);
    float b = lane(bp, vec3(.3,.3,8.)*u, 7.1*u, .71);
    float c = lane(cp, vec3(15,15,5.1)*u,        51.1*u, .8);

    return min(c,min(a,b));


