void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  //vec2 p=(fragCoord.xy*2.0-iResolution.xy)/iResolution.y;
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);


  // nav

  float tt = iTime * .03;

  vec3 s = lattice(tt)*15.1;
  s.xz *= rot(tick(tt)*.413);
  s.xy *= rot(tick(tt)*.111);

  vec3 t = lattice(tt + 33.)*15.1;
  t.xz *= rot(tick(-tt)*.31);
  t.yz *= rot(tick(tt)*.21);


  vec3 arm = vec3(0,0,1);
  arm.xz *= rot(tt);
  arm.yz *= rot(tt*.71);
  arm.xy *= rot(tt*.51);

  s += arm * 1.;
  t += arm * 1.;


  s += tock(tt * 3.) * 100. * arm;
  t += tock(tt * 3.) * 100. * arm;



  vec3 cz=normalize(t-s);
  vec3 cx=normalize(cross(cz,vec3(0,1,0)));
  vec3 cy=normalize(cross(cz,cx));


  float fov =  .8;
  vec3 r=normalize(cx*uv.x+cy*uv.y+cz*fov);

  r.z -= dot(uv,uv)/10.;


  vec3 p=s;
  float d = 0.;
  float dd = 0.;
  float edge = 0.;
  float i = 0.;
  vec3 n1,n2;

  bool hit = false;


  // march

  for(float nn=0.;nn<200.;nn++){ 
    i =nn;
    d=map(p);

    if ( d < .001 ) {
      hit = true;
      break;
    }


    if ( dd > 100.) {
        break;
    }
    p += r * d;
    dd += d;

  }





  vec3 col;

  col = vec3(.8,.4,.2);

  float ao = pow(1. - i/100.,.5);

  col *= ao;

  col *= 1.7;




  n2=norm(p, vec2(0.0, 1.0E-2) );
  n1=norm(p, vec2(0.0, 2.50E-2) );
  edge=saturate(length(n1-n2)/0.1);
  col -= edge*100.;


  if ( ! hit ) { 
      vec3 hue = 1. - vec3(.8,.5,.2);
      col = mix( vec3(.5), hue, r.x);
      col = mix(vec3(0), vec3(1.), pow(dot(r,normalize(vec3(1,0,0))*.5+.5),.4));

  }




  fragColor = vec4(col, 1.0);

}


