  vec3 t =  vec3(0,0,0);



  s *= 1.;(sin(tick(tt*.21)) * .5 + .5) +1.;
  float t1 =tock(tt * 1.21);
  float t2 =tock(tt * 1.0);
  float t3 =tock(tt * .81);
  s.yz *= rot(t1);
  s.xz *= rot(t2);




  vec3 cz=normalize(t-s);
  vec3 cx=normalize(cross(cz,vec3(0,1,0)));
  vec3 cy=normalize(cross(cz,cx));
      // fisheye
  cz += dot(uv,uv)/5.;


  vec3 r=normalize(cx*uv.x+cy*uv.y+cz * .8);

  bool hit = false;
  float d;
  float edge = 0.;
  float dd=0.;
  float i = 0.;
  vec3 p=s;
  vec3 n1,n2;



  // march

  for(float z=0.;z<100.;z++){ 

    i = z;
    d = map(p);

    if ( d < .001 ) {
      hit = true;
      break;
    } 

    dd += d;
    p += r * d;

  }



  // hue

  vec3 col = vec3(.8, .5, .2);
  float ao = i/100.;
  col -= ao ;


  n2 = norm(p, vec2(0.0, 1E-2 ));// + 3E-2*.01) );
  n1 = norm(p, vec2(0.0, 1.4E-2) );


  edge = saturate(length(n1-n2)/0.1);

  col -= edge;


  if (! hit){
      col = vec3(.93, .95, .90);
      col = vec3(1);
  }

  fragColor = vec4(col, 1.0);

}

