#define TMIN 0.01
#define TMAX 200.
#define RAYMARCH_TIME 128
#define PRECISION .001
#define AA 3
#define PI 3.1415926

#define S(v,r) smoothstep( r, r+ 3./iResolution.y, v )
float logo(vec2 uv);

//========SDFunctions========
float sdSphere(vec3 p, vec3 o, float r){
    return length(p-o)-r;
}
float sdCapsule(vec3 p,vec3 a, vec3 b,float r){
    vec3 ab = b - a;
    vec3 ap = p - a;
    float t = dot(ab, ap)/dot(ab,ab);
    t = clamp(t,0.,1.);
    vec3 c = a +t*ab;
    return length(p-c)-r;
}
float sdTorus(vec3 p,vec3 o, float r1,float r2){
    p -= o;
    return length( vec2(length(vec2(p.x,p.z))-r1,p.y) ) - r2;
}
float sdBox(vec3 p, vec3 o, vec3 s){
    p-=o;
    return length(max(abs(p)-s,0.)) -.05;
}
float sdCylinder(vec3 p, vec3 a, vec3 b, float r) {
	vec3 ab = b-a;
    vec3 ap = p-a;
    float t = dot(ab, ap) / dot(ab, ab);
    vec3 c = a + t*ab;

    float x = length(p-c)-r;
    float y = (abs(t-.5)-.5)*length(ab);
    float e = length(max(vec2(x, y), 0.));
    float i = min(max(x, y), 0.);

    return e+i-.05;
}
//===============TRANSFORM=================
mat2 rotate(float a){
return mat2(cos(a),sin(a),-sin(a),cos(a));
}


//===============RENDER===================
//Scene
float f(vec3 p){
    float ds = sdSphere(p,vec3(0,1,0),1.);//sdf sphere
    float dc = sdCapsule(p,vec3(1,.5,-1),vec3(3.5,.5,0),.5);//sdf capsule
    float dt = sdTorus(p,vec3(-2,.5,1),1.,.5);//sdf torus
    float db = sdBox(p,vec3(1,1,2),vec3(1,2,1));//sdf box
    float dcy = sdCylinder(p,vec3(1.5,1.7,-1),vec3(4,.5,2),.5);//sdf cylinder
    float d = min(ds,dc);
    d = min(d,dt);
    d = min(d,db);
    d = min(d,dcy);
    return d;
}
float rayMarch(in vec3 ro, in vec3 rd) {
    float t = TMIN;
    for(int i = 0; i < RAYMARCH_TIME ; i++) {
        vec3 p = ro + t * rd;
        float d = f(p);
        t += d;
        if(d < PRECISION || t > TMAX)
            break;
    }
    return t;
}
float softShadow(in vec3 ro, in vec3 rd,float k){
    float t = .1;
    float res = 1.;
    float ld = 1e20;
    for(int i = 0; i < RAYMARCH_TIME ; i++) {
        vec3 p = ro + t * rd;
        float d = f(p);
        //res = min(res, k*d/t);
        float y = (i==0) ? 0.0 : d*d/(2.0*ld); 
        float l = sqrt(d*d-y*y);
        res = min(res, k*l/max(0.0,t-y));
        ld = d;
        t += d;
        if(d < PRECISION || t > 30.)
            break;
    }
    return clamp(res,0.,1.);
}
float calAO( in vec3 p, in vec3 n){
    float occ = 0.;
    float sca = 1.;
    for(int i = 0;i<5;i++){
        float h = 0.001 + 0.4*float(i)/4.0;
        float d = f(p + h*n);
        occ += (h-d)*sca;
        sca *= 0.6;
        }
    return clamp(1.-1.5*occ,0.,1.);
}
// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal(in vec3 p) {
    const float h = 0.0001;
    const vec2 k = vec2(1, -1);
    return normalize(k.xyy * f(p + k.xyy * h) +
        k.yyx * f(p + k.yyx * h) +
        k.yxy * f(p + k.yxy * h) +
        k.xxx * f(p + k.xxx * h));
}

mat3 setCamera(in vec3 camtar, in vec3 campos, in float camro){
    vec3 z = normalize(camtar-campos);
    vec3 cp = vec3(sin(camro),cos(camro),0.);
    vec3 x = normalize(cross(cp,z));
    vec3 y = cross(z,x);
    return mat3(x,y,z);
}
vec3 render(vec2 uv){
    vec3 lightPos = vec3(-5., 5.,-5);//light

    //SET Camera
    vec3 cam_tar = vec3(1,0,2);//cam target
    vec3 cam_pos = cam_tar + vec3(10.*cos(iTime*.5), 10., 10.*sin(iTime*.5));//cam position

    vec3 rd = vec3(uv,3.); //decide view width
    if(iMouse.x!=0. || iMouse.y!=0. ){
        float angle_x = -(iMouse.x /iResolution.x-0.5)  *0.5*PI;
        float angle_y = (iMouse.y /iResolution.y-0.5)  *0.5*PI;
        rd.yz *= rotate(angle_y);
        rd.zx *= rotate(angle_x);}
    rd = normalize(setCamera(cam_tar,cam_pos,0.)*rd);//viewing frustum

    float t = rayMarch(cam_pos,rd);//raymarching

    vec3 color = texture(iChannel0,rd).rgb;//background
    if(t > TMAX) return color; 

    vec3 p = cam_pos + t*rd;
    vec3 n = calcNormal(p);
    vec3 l = normalize(lightPos-p);//lightDir
    vec3 h = normalize(l-rd);//

    vec3 diffusecol = vec3(1.);//diffuse color
    vec3 specol = vec3(1.);//specular color
    vec3 cmrfl = 1.5*vec3(.5)*texture(iChannel0,reflect(rd,n)).rgb;//cubemap reflection
    vec3 cmrfr = texture(iChannel0,refract(rd,n,.8)).rgb;//cubemap refraction


    float dif = clamp(dot(l,n),0.,1.);//diffuse    
    float spe = pow(clamp(dot(h,n),0.,1.),50.);//specular

    float shadow = softShadow(p,l,8.);//soft shadow
    float occ = calAO(p,n);//AO
    float fresnel = pow(clamp(1. - dot(n,-rd),0.,1.),5.);

    if(floor(mod(iTime*.5,2.))==0.){
        color = cmrfl + .2*dif*diffusecol + spe*specol;//metal
        color *= clamp(shadow+.4,0.,1.)*occ;
        color += fresnel*.4;}
        else{
        color = cmrfr + .1*dif*diffusecol + smoothstep(.7,1.,spe)*specol;//glass
        color *= clamp(shadow+.8,0.,1.)*occ;
        color += fresnel*.8;}
    // fog
    //color *= exp( -0.0001*t*t*t );

    return color;
}
vec2 getuv(vec2 coord){
    return (2.*coord-iResolution.xy)/iResolution.y;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec3 color = vec3(0.);
    #if AA>1
    for(int m = 0; m < AA; m++) {
        for(int n = 0; n < AA; n++) {
            vec2 offset = 2. * (vec2(float(m), float(n)) / float(AA) - .5);
            vec2 uv = getuv(fragCoord + offset);
    #else
            uv = getuv(fragCoord);
    #endif
            color += render(uv);
    #if AA>1
        }
    }
    color /= float(AA*AA);
    #endif


    color = mix(color,vec3(1),logo(uv));
    fragColor = vec4(color,1.0);
}


float logo(vec2 uv){
float n = 0.;
n += S(.05,abs(length(uv-vec2(-1.23,0))-.12));
n *= 1.-S(-1.25,uv.x);
n += S(.17,abs(uv.y))*S(.05,abs(uv.x+1.29));
n += S(.05,abs(length(uv-vec2(-1.5,0))-.12));
n += S(.05,abs(length(uv-vec2(-0.9,0))-.12));
n += (1.+S(-.6,uv.x)-S(.05,uv.y))*S(.05,abs(length(uv-vec2(-0.6,.03))-.09));
n += (1.-S(-.6,uv.x)+S(-.05,uv.y))*S(.05,abs(length(uv-vec2(-0.6,-.04))-.09));
return n;
}

//change these constants to get different patterns!
#define iterations 2
#define modulo 3


#define c1 vec3(1.,0.5,1.5)

vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 4.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
