    float scale = c1.z;
    vec2 t2 = vec2(0.);
    vec2 t3 = vec2(0.);   
    for(int k = 0; k < 6*iterations; k++){
        uv -= (t2.yx)/(scale)+float(k%modulo);
        t2 = triangle_wave(uv.yx-.5,scale);
        //uv = min(uv,t2);
        t3 = -triangle_wave(uv,scale);
        //t3 /= float(1+k%3);
        uv.yx = -(t2+t3);
        col.x = 1.-abs(-uv.y+uv.x+col.x);
        //col.x = 1.-abs(uv.y+uv.x-col.x);
        col = col.yzx;
        uv /= scale*scale;
        //uv /= scale*(scale)+uv.x-uv.y;
        //uv *= (t2.y-t2.x+1.);
    }
    fragColor = vec4(col,1.0);
}

