}

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

vec3 distort(vec3 p) {
    float o = 2.* pi / 3.;
    float t = 3. * length(p) - 0.5 * iTime;
   // p = abs(p) - 0.5;
    p.xy *= Rot(t - o);
    p.yz *= Rot(t);
    p.zx *= Rot(t + o);
    return fract(0.8 * p) - 0.5;
}

float GetDist(vec3 p) {

    p = distort(p); 
    float d = length(p.xz) - 0.5;

    // lower k => more "fog"
    float k = 0.25;
    //return length(p) -0.3 + SURF_DIST;
    return k * length(vec2(d, p.y)) + 1. * SURF_DIST;
}

