// continuing https://shadertoy.com/view/ss2BRh
// which just renormalize statistics when mixing stochastic fields ( textures, noises, etc )
// Here we also restore the law distribution back, by recovering the histogram 
// ( just uniform here. For general case see https://hal.inria.fr/hal-01824773 )

void mainImage( out vec4 O, vec2 U )
{
    vec3 R = iResolution;
    float v,z = iMouse.z > 0. ? 1. : 3.,
          t = mod(iTime,2.); t = min(t,2.-t);          // saw interpolant  
    O = U.y > R.y/2. 
          ? texelFetch(iChannel0, ivec2(U/z)%256, 0 )  // top: white noise
          : texelFetch(iChannel1, ivec2(U/z)%256, 0 ); // bottom: blue noise

    if (    int(U)/2 == int(R)/6                       // red separators
         || int(U)/2 == int(R)/3              
         || int(U.y)/2 == int(R.y)/4 )
      { O = vec4(1,0,0,1); return; }

    if ( U.x < R.x/3. )                       // --- left: naive interpolation
        v =  mix( O.x , O.y, t);

    else  if ( U.x < R.x*2./3. ){             // --- middle: interpolation with moments renormalization
        O = (O-.5) / .289 ;                   // move to 0-mean, 1-variance
        // go-to 1-variance is only required when mixing 2 different statistics
        v = mix( O.x , O.y, t);
        v /=  sqrt(2.*t*t-2.*t+1.);           // renormalize from variance =  (1-t)² + t²
        v = .5 + .289 * v;                    // back to target (here, initial) statistics
    }
    else {                                    // right: restore the uniform distribution
        v = mix( O.x , O.y, t);
        float m = min(t,1.-t), T = 2.*t*(1.-t); // apply cumulative distrib as histogram
        v = v < m ? v*v/T : v < 1.-m ? m*m/T + (v-m)/(1.-m) : 1. - (1.-v)*(1.-v)/T;
    }
    if ( iMouse.z > 0. ) v = clamp(v,0.,1.);
    O = v < 0. ? vec4(1,0,0,0)                // red/green = under/over shoots
      : v > 1. ? vec4(0,1,0,0)
      :          vec4( pow(v, 1./2.2) );      // to sRGB
}

