        for (int k = 0; k < 20; k++) {
          accum2 += max(length(cam2) - 6260., 0.) * length(div2);

          cam2 += div2;
        }

        vec3 t1 = BRrgb * accum222 + BMrgb * accum2;
        vec3 t2 = BRrgb * accum + BMrgb * accum1111;

        accumulateLight +=
            (exp(-pow(t1 * 0.0054, vec3(1.))) * exp(-t2 * 0.028)) *
