// At least renormalize statistics when mixing stochastic fields ( textures, noises, etc )
// If you also want to have the law distribution back, you have to recover the histogram: see https://hal.inria.fr/hal-01824773 
// Uniform case treated here: https://www.shadertoy.com/view/sdSfRw

void mainImage( out vec4 O, vec2 U )
{
    vec3 R = iResolution;
    O = U.y > R.y/2. 
          ? texelFetch(iChannel0, ivec2(U/3.)%256, 0 ) // top: white noise
          : texelFetch(iChannel1, ivec2(U/3.)%256, 0 );// bottom: blue noise
    float v,t = mod(iTime,2.); t = min(t,2.-t); // saw interpolant

    if (    int(U)/2 == int(R)/4              // red separators
         || int(U.y)/2 == int(R.y)/4 )
      { O = vec4(1,0,0,1); return; }

    if ( U.x < R.x/2. )                       // --- left: naive interpolation
        v =  mix( O.x , O.y, t);

    else {                                    // --- right: interpolation with moments renormalization
        O = (O-.5) / .289 ;                   // move to 0-mean, 1-variance
        // go-to 1-variance is only required when mixing 2 different statistics
        v = mix( O.x , O.y, t);
        v /=  sqrt(2.*t*t-2.*t+1.);           // renormalize from variance =  (1-t)² + t²
        v = .5 + .289 * v;                    // back to target (here, initial) statistics
    }
    O = vec4( pow(v, 1./2.2) );               // to sRGB
}

mat2 rot( float a ){ return mat2( sin(a),  cos(a), -cos(a),  sin(a) ); }

float noise( in vec2 x ){ return smoothstep(0.,1.,sin(1.5*x.x)*sin(1.5*x.y)); }

float fbm( vec2 p ){

    mat2 m = rot(.4);
    float f = 0.0;
    f += 0.500000*(0.5+0.5*noise( p )); p = m*p*2.02;
    f += 0.250000*(0.5+0.5*noise( p )); p = m*p*2.03;
    f += 0.125000*(0.5+0.5*noise( p )); p = m*p*2.01;
    f += 0.015625*(0.5+0.5*noise( p ));
    return f/0.96875;
}


float pattern (in vec2 p, out vec2 q, out vec2 r, float t){


	q.x = fbm( 2.0*p + vec2(0.0,0.0) + 2.*t );
    q.y = fbm( 1.5*p + vec2(5.2,1.3) + 1.*t );

    r.x = fbm( p + 4.*q + vec2(1.7,9.2) + sin(t) + .9*sin(30.*length(q)));
    r.y = fbm( p + 8.*q + vec2(8.3,2.8) + cos(t) + .9*sin(20.*length(q)));

    return fbm( p + 7.*r*rot(t) );

}

void mainImage(out vec4 C, in vec2 U){

    vec2 uv = (U.xy-iMouse.xy)/iResolution.xy * 2.;
    uv.x *= iResolution.x/iResolution.y;

    vec2 q,r;
    vec3 col1 = vec3(.9,.7,.5);
    vec3 col2 = vec3(.3,.5,.4);
    vec3 c;

    float f = pattern(uv, q, r, 0.1*iTime);

    //mix colours
    c = mix(col1, vec3(0), pow(smoothstep(.0,.9,f), 2.));
    c += col2 * pow(smoothstep(0., .8, dot(q,r)*.6), 3.) * 1.5;
    //add contrast
    c *= pow(dot(q,r) + .3, 3.);
    //soften the bright parts
    c *= f*1.5;

    //c += vec3(1.7,1.2,1.2) * dot(q,r);
    //c += vec3(.2) * smoothstep(0., .2,pow(length(q),3.));
    //c += dot(q,r);
    //c += smoothstep(0.,3.,pow(length(df),0.12));


    C = vec4( c, 1. );
}

#define R(p,a,t) mix(a*dot(p,a),p,cos(t))+sin(t)*cross(p,a)
#define H(h) (cos((h)*1.3+vec3(10,23,21))*.5+.5)

void mainImage(out vec4 O, vec2 C)
{
    vec3 p,r=iResolution,c=vec3(0),
    d=normalize(vec3((C-.5*r.xy)/r.y,1));
 	for(float i=0.,s,e,g=0.,t=iTime;i++<90.;){
        p=g*d;
        p.z-=.1;
        p=R(p,H(t*.03),t*.2);
        p.x-=.1;
        p=acos(sin(abs(p)));
        s=(mod(sqrt(iTime),3.) + 3.)/p.x;
        for(int j=0;j++<15;)
           s*=e=max(.5/dot(p,p),1.9),
           p=abs(p*e-vec3(2,3,2)),
           p=R(p,normalize(vec3(3,3,1)),1.8);
        g+=e=abs(p.z)/s+1e-4;
	    c+=mix(vec3(1),H(log(s)*.4),.25)*.03/exp(0.8*i*i*e);
	}
	c*=c*c*c;
    O=vec4(c,1);
}

