vec2 hash( vec2 p ) // replace this by something better
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) );
}

// -----------------------------------------------

mat2 Rot(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
	float f = 0.0;

	uv *= 4.0;
    float a = 3.14159 + 0.5 * thc(4., iTime);
    float c = cos(a), s = sin(a);
    mat2 rot = mat2(c, s, -s, c);

    float m = 0.5;
    float n = 6.;

    float am = 10.;
    float fr = fract(0.8 * iTime / am);
    float poop = 1.5 + floor(am * fr); // didnt know what to call this

    // (meant to use fract(0.8 * iTime) instead of fr, but I like how it looks)
    poop = mix(poop, poop + 1., fr);
    float test = poop * step(0.5, fract(0.25 + 0.5 * uv.y + 0.05 * cos( iTime + 0.2 * uv.x))) - 1.;
    for (float i = 0.; i < n; i++) {
        a = (n - i) / n * 0.006 * iTime;
        uv += 10. * a * vec2(cos(a), sin(a));
        //uv *= Rot(a);
        f += m * noise(uv); //0.5 * abs(f - m * noise( uv ));   
        uv += (5. + 0.45 * cos(uv.x * 2. + test + 0.2 * iTime)) * cos(f);
        //m *= 0.5;
    }
    //f *= 0.5;
	f = 0.5 + 0.5*f;
    f = smoothstep(0., 1., f);
    float k = 6.;
	f = smoothstep(-k, k, f);
    //f *= smoothstep( 0.0, 0.005, abs(p.x-0.6) );	

    vec3 e = vec3(1);
    vec3 col = pal(0.755 + test * f, e, e, e, 0.36 * vec3(0,1,2)/3.);
    float mx = 0.5 + 0.5 * thc(4.,0.12 * uv.x + iTime);
    col *= mix(1., test, mx);
    col = sqrt(col) + 0.05;
	fragColor = vec4(col, 1.0 );
}

