// To reverse direction so that red is further away from the center and blue
// is closer, just subtract your LEVEL from 2.0

// BLUE_EDGE if false (green will always stay at the original position)
#define RED_EDGE (false)

// Level Of chromatic Aberration
#define LEVEL (1.02)


// LEAVE AS IS FOR BEST EXPERIENCE
#define LOA (RED_EDGE ? 2.0 - (LEVEL) : (LEVEL))
#define LINEAR 0


vec2 aberrate(vec2 v, float level) {
    return (v - 0.5) / level + 0.5;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    float r, g, b;

#if LINEAR == 1
    r = texture(iChannel0, uv - (LOA - 1.01)).r;
    g = texture(iChannel0, uv).g;
    b = texture(iChannel0, uv + (LOA - 1.01)).b;
#else
    r = texture(iChannel0, aberrate(uv, 1.0 / LOA)).r;
    g = texture(iChannel0, uv).g;
    b = texture(iChannel0, aberrate(uv, LOA)).b;
#endif

    /*
    float r = texture(iChannel0, aberrate(uv, 1.0 / LOA)).r;
    float g = texture(iChannel0, aberrate(uv, 1.0)).g;
    float b = texture(iChannel0, aberrate(uv, LOA)).b;
    */

    // Output to screen
    fragColor = vec4(r, g, b, 1.0);
}

