#define TAU 6.2831853071

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / iResolution.xy;

    float o = texture(iChannel1, uv * 0.25 + vec2(0.0, iTime * 0.025)).r;
    float d = (texture(iChannel0, uv * 0.25 - vec2(0.0, iTime * 0.02 + o * 0.02)).r * 2.0 - 1.0);

    float v = uv.y + d * 0.1;
    v = 1.0 - abs(v * 2.0 - 1.0);
    v = pow(v, 2.0 + sin((iTime * 0.2 + d * 0.25) * TAU) * 0.5);

    vec3 color = vec3(0.0);

    float x = (1.0 - uv.x * 0.75);
    float y = 1.0 - abs(uv.y * 2.0 - 1.0);
    color += vec3(x * 0.5, y, x) * v;

    vec2 seed = fragCoord.xy;
    vec2 r;
    r.x = fract(sin((seed.x * 12.9898) + (seed.y * 78.2330)) * 43758.5453);
    r.y = fract(sin((seed.x * 53.7842) + (seed.y * 47.5134)) * 43758.5453);

    float s = mix(r.x, (sin((iTime * 2.5 + 60.0) * r.y) * 0.5 + 0.5) * ((r.y * r.y) * (r.y * r.y)), 0.04); 
    color += pow(s, 70.0) * (1.0 - v);

    fragColor.rgb = color;
    fragColor.a = 1.0;
}

#define pi 3.14159

// sloppy (used in rand)
#define time() 0.2 * iTime 

mat2 Rot(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, -s, s, c);
}

float sdEquilateralTriangle( in vec2 p )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - 1.0;
    p.y = p.y + 1.0/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0, 0.0 );
    return -length(p)*sign(p.y);
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

// draws an arrow (d is used to make it go up + down)
float arrow(vec2 uv, float d) {
    float h = 0.1;//0.1 + 0.4 * thc(4.,2. * d);
    float d1 = sdEquilateralTriangle(uv-vec2(0.,0.25 - h));
    float s1 = step(d1, -0.5);

    float d2 = sdBox(uv - vec2(0.,-h), vec2(0.05,0.2));
    float s2 = step(d2, 0.);

    return max(s1, s2);
}

float h21 (vec2 a, float zoom) {
    a = mod(a, zoom);
    return fract(sin(dot(a, vec2(12.9898, 78.233)))*43758.5453123);
}

float rand(vec2 ipos, vec2 fpos, float sc) {
    //float val = 20. * length(fpos-0.5) + h21(ipos, sc) - iTime;
    float val = h21(ipos, sc) + time();

    float a = 6.28319 * h21(ipos, sc);
    vec2 dir = vec2(cos(a), sin(a));

    float f = floor(val);

    float v1 = h21(0.01 * ipos + dir * f, sc);
    float v2 = h21(0.01 * ipos + dir * (f + 1.), sc);  

    return mix(v1, v2, smoothstep(0., 1., fract(val)));
}

float rand2(vec2 ipos, vec2 fpos) {
    // Generate values for each corner of uv
    float sc2 = 301.; // can be anything 
    float l  = rand(ipos + vec2(1,0), fpos, sc2);
    float t  = rand(ipos + vec2(0,1), fpos, sc2);
    float tl = rand(ipos + vec2(1,1), fpos, sc2);
    float id = rand(ipos + vec2(0,0), fpos, sc2);

    // Smooth fpos so boundaries meet smoothly
    vec2 sfpos = fpos * fpos * (3. - 2. * fpos);

    // Box lerp between the corner values
    return l  * sfpos.x      * (1.-sfpos.y)
     	 + t  * (1.-sfpos.x) * sfpos.y
         + tl * sfpos.x      * sfpos.y
         + id * (1.-sfpos.x) * (1.-sfpos.y);   
}

vec2 movingTiles(vec2 uv, float sc, float spd){
    float time = spd * iTime;

    vec2 ipos = floor(sc * uv);
    vec2 fpos = fract(sc * uv);
    float h = rand2(ipos, fpos);

    float ft = 0.5 + 0.5 * cos(0. * atan(uv.x, uv.y) + 4. * uv.x - uv.y + 0. * length(uv) + 0.2 * pi * h + 4. * time);//fract(h*0.5 + time);


   // ft = fract(time);
   // ft = smoothstep(0., 1., ft);


    uv *= sc;

    float k = step(0.5, ft);//smoothstep(-0.,0.5, ft - 0.5);
    uv.x +=      k * sign(fract(uv.y * 0.5) - 0.5) * ft * 2.;
    uv.y += (1.-k) * sign(fract(uv.x * 0.5) - 0.5) * ft * 2.;

    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float sc = 10.;

    // Cut uv into smaller uvs
    vec2 ipos = floor(sc * uv);
    vec2 fpos = fract(sc * uv);   

    float num = 301.; // can be anything

    float v = rand2(ipos, fpos);
    //v *= step(v, 0.55);

    // Do the tile pattern
    uv = movingTiles(uv, sc, -0.2);
    uv *= 2.;

    ipos = floor(uv) + 0.5;
    fpos = fract(uv) - 0.5;


    // Generate circle using radius we've obtained
    float d = length(fpos);
    float k = 10. / iResolution.y;
    float s = smoothstep(-k, k, -d + 0.5 * v);

    float w = 0.1 * v;
    fpos *= Rot(2. * pi * v);
    s = max(step(abs(fpos.x), w), step(abs(fpos.y), w));
    s = step(mlength(fpos), 0.5 * v);

    //if (v > 0.5)
        fpos = abs(fpos) - 0.21;
    s = arrow(fpos, 0.);
    vec3 col = vec3(s);

    vec3 e = vec3(1.);
    col *= 4. * pal(h21(ipos), e, e, e, 0.22 * vec3(0,1,2)/3.);
    col = clamp(col, 0., 1.);
    //col += 0.4 * (1.-s) * pal(v, e, e, e, 0.2 * vec3(0,1,2)/3.);
    col = mix(col, h21(ipos) *  s * vec3(fpos, 2.), 0.5 + 0.5 * thc(7., length(ipos)/sc - 0.5* iTime));
   // col = vec3(fpos + 0.5, 1.);
    col += 0.1;
    fragColor = vec4(col,1.0);
}

