/*
Unlike the polygon drawing algorithm in my previous shader the algorithms for drawing these primitives
with Gaussian are rather simple. The line, dot and rectangle formulas match exactly what Gaussian
filtering would yield, the circle algorithms on the other hand might not be quite accurate, but they
look about right. The line, dot and thin circle algorithms represent Gaussian filtering of infinitely
thin lines and dots, therefore their intensities need to be modified according to the blurring radius.
The full circle and rectangle algorithms on the other hand naturally scale themselves.
The full circle is probably too bright when blurred a lot, I'd need to look into Gaussian circles more.
*/

float sq(float x)
{
    return x * x;
}

float gaussian(float x)
{
	return exp(-x*x);	// ~6 FR
}

float erf_fast(float x)
{
	float y, xa = abs(x);

	// erf(x) ~= 1 - exp(<polynomial>) for x >= 0, max error 1/4249
	// 2 mad, 1 mul, 1 +-, 1 copysign, 1 exp = ~9 FR
	y = ((-0.06388*xa - 0.66186)*xa - 1.123613)*xa;
	y = 1. - exp(y);
	y *= sign(x);

	return y;
}

float lsrgb(float l)	// converts a [0.0, 1.0] linear value into a [0.0, 1.0] sRGB value
{
	float x, line, curve;

	// 13 FR every time + 2 FR once
	line = l * 12.92;	// 1 FR
	x = sqrt(l);	// 4 FR
	curve = ((((0.455*x - 1.48)*x + 1.92137)*x - 1.373254)*x + 1.51733216)*x - 0.0404733783;	// 5 FR + 2 FR once, error 0.145 sRGB units

	return l <= 0.0031308 ? line : curve;	// 3 FR
}

vec2 point_rot(vec2 p, float costh, float sinth)
{
    vec2 rp;
	rp.x = p.x * costh - p.y * sinth;
	rp.y = p.x * sinth + p.y * costh;
    return rp;
}

float thin_line_weight(float r1x, float r1y, float r2x, float costh, float sinth, vec2 pf)
{
	float v;
	vec2 rp;

    rp = point_rot(pf, costh, sinth);
	v = (erf_fast(rp.x-r1x) - erf_fast(rp.x-r2x)) * 0.5;
	v *= gaussian(rp.y-r1y);

	return v;
}

float point_weight(vec2 dp, float rad, vec2 pf)
{
	float d = distance(dp, pf) * rad;	// distance of the pixel from the centre of the dot, scaled

	return gaussian(d);
}

float circle_full_weight(vec2 pc, float circrad, float rad, vec2 pf)
{
	float dc, dn, df;

	dc = distance(pf, pc);		// Distance to center
	dn = (circrad - dc) * rad;	// Near edge distance
	df = -(circrad + dc) * rad;	// Far edge distance

	return (erf_fast(dn) - erf_fast(df)) * 0.5;
}

float circle_hollow_weight(vec2 pc, float circrad, float rad, vec2 pf)
{
	float dc, dn, df;

	dc = distance(pf, pc);		// Distance to center
	dn = (circrad - dc) * rad;	// Near edge distance
	df = -(circrad + dc) * rad;	// Far edge distance

	return gaussian(dn) + gaussian(df);
}

float rect_full_weight(vec2 p0, vec2 p1, float rad, vec2 pf)
{
	float d;
	vec2 d0, d1, gv;

	d0 = (pf - p0) * rad;
	d1 = (pf - p1) * rad;

	gv.x = erf_fast(d0.x) - erf_fast(d1.x);
	gv.y = erf_fast(d0.y) - erf_fast(d1.y);

	return gv.x * gv.y * 0.25;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{    
    // Pixel to world coordinates
    float scrscale = 18. / iResolution.y;
    vec2 uv = (fragCoord-iResolution.xy*0.5) * scrscale;

    // Tetragon rotation
    float th = iTime * 6.2831853 / 40.;
    float costh = cos(th);
    float sinth = sin(-th);

    // Varying blurriness
    float rad = sq(1.-abs(cos(iTime * 6.2831853 / 16.))) * 6.;
    float min_rad = 0.8*scrscale;
    rad = length(vec2(rad, min_rad));

    // Add all the primitives
    vec3 pv = vec3(0.);
    pv += thin_line_weight(-3.5/rad, 0./rad, 5./rad, costh/rad, -sinth/rad, uv) * min_rad/rad;
    pv += vec3(0., 0.44, 1.) * circle_full_weight(point_rot(vec2(-5., 0.), costh, sinth), 1.5, 1./rad, uv);
    pv += vec3(0.5, 1., 0.) * circle_hollow_weight(point_rot(vec2(5., 0.), costh, sinth), 1.5, 1./rad, uv) * min_rad/rad;
    for (float dt=-3.25; dt <= 3.25; dt += 0.5)
        pv += mix(vec3(1., 1., 0.), vec3(1., 0., 0.), (dt+3.5)/7.) * point_weight(point_rot(vec2(0., dt), costh, sinth), 1./rad, uv) * sq(min_rad/rad);
    pv += vec3(1., 0., 0.06) * rect_full_weight(vec2(-1.5, 3.5), vec2(1.5, 6.5), 1./rad, point_rot(uv, costh, -sinth));
    pv += vec3(1., 1., 0.) * 
            (rect_full_weight(vec2(-1.5, -6.5), vec2(1.5, -3.5), 1./rad, point_rot(uv, costh, -sinth)) -
            rect_full_weight(vec2(-1., -6.), vec2(1., -4.), 1./rad, point_rot(uv, costh, -sinth)));

    // Output to screen
    fragColor = vec4(lsrgb(pv.r), lsrgb(pv.g), lsrgb(pv.b), 1.);
}

#define TMIN 0.01
#define TMAX 200.
#define RAYMARCH_TIME 128
#define PRECISION .001
#define AA 3
#define PI 3.1415926

#define S(v,r) smoothstep( r, r+ 3./iResolution.y, v )
float logo(vec2 uv);

//========SDFunctions========
float sdSphere(vec3 p, vec3 o, float r){
    return length(p-o)-r;
}
//===============TRANSFORM=================
mat2 rotate(float a){
return mat2(cos(a),sin(a),-sin(a),cos(a));
}
float smUni( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

//===============RENDER===================
//Scene
float f(vec3 p){
    p.zx*=rotate(iTime);
    p.yz*=rotate(iTime);
    float k = .7;
    float d1 = sdSphere(p,vec3(sin(iTime*.5)),1.3);//center sphere
    float d2 = sdSphere(p,vec3(0,2.*sin(iTime),0.),.5);
    float d = smUni(d1,d2,k);

    float d3 = sdSphere(p,vec3(sin(iTime+5.),0,2.*sin(iTime+5.)),.9);
    d= smUni(d,d3,k);

    float d4 = sdSphere(p,vec3(-cos(iTime+.3),cos(iTime+.3),cos(iTime+.3)),.8);
    d= smUni(d,d4,k);

    float d5 = sdSphere(p,vec3(3.*cos(iTime+.7),0.,.5),.3);
    d= smUni(d,d5,k);


    return d;
}
float rayMarch(in vec3 ro, in vec3 rd) {
    float t = TMIN;
    for(int i = 0; i < RAYMARCH_TIME ; i++) {
        vec3 p = ro + t * rd;
        float d = f(p);
        t += d;
        if(d < PRECISION || t > TMAX)
            break;
    }
    return t;
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal(in vec3 p) {
    const float h = 0.0001;
    const vec2 k = vec2(1, -1);
    return normalize(k.xyy * f(p + k.xyy * h) +
        k.yyx * f(p + k.yyx * h) +
        k.yxy * f(p + k.yxy * h) +
        k.xxx * f(p + k.xxx * h));
}

mat3 setCamera(in vec3 camtar, in vec3 campos, in float camro){
    vec3 z = normalize(camtar-campos);
    vec3 cp = vec3(sin(camro),cos(camro),0.);
    vec3 x = normalize(cross(cp,z));
    vec3 y = cross(z,x);
    return mat3(x,y,z);
}
vec3 render(vec2 uv){
    vec3 lightPos = vec3(-5., 5.,-5);//light

    //SET Camera
    vec3 cam_tar = vec3(-1,3,2);//cam target
    vec3 cam_pos = cam_tar +vec3(-3,8,5);//cam position


    vec3 rd = vec3(uv,9.); //decide view width
    rd = normalize(setCamera(cam_tar,cam_pos,0.)*rd);//viewing frustum

    float t = rayMarch(cam_pos,rd);//raymarching

    vec3 color = vec3(0);//background
    if(t > TMAX) return color; 

    vec3 p = cam_pos + t*rd;
    vec3 n = calcNormal(p);

    color = n*.5+.5;
    // fog
    color *= exp( -0.0001*t*t*t );

    return color;
}
vec2 getuv(vec2 coord){
    return (2.*coord-iResolution.xy)/iResolution.y;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec3 color = vec3(0.);
    #if AA>1
    for(int m = 0; m < AA; m++) {
        for(int n = 0; n < AA; n++) {
            vec2 offset = 2. * (vec2(float(m), float(n)) / float(AA) - .5);
            vec2 uv = getuv(fragCoord + offset);
    #else
            uv = getuv(fragCoord);
    #endif
            color += render(uv);
    #if AA>1
        }
    }
    color /= float(AA*AA);
    #endif



    color = mix(color,vec3(1),logo(uv));
    fragColor = vec4(color,1.0);
}


float logo(vec2 uv){
float n = 0.;
n += S(.05,abs(length(uv-vec2(-1.23,0))-.12));
n *= 1.-S(-1.25,uv.x);
n += S(.17,abs(uv.y))*S(.05,abs(uv.x+1.29));
n += S(.05,abs(length(uv-vec2(-1.5,0))-.12));
n += S(.05,abs(length(uv-vec2(-0.9,0))-.12));
n += (1.+S(-.6,uv.x)-S(.05,uv.y))*S(.05,abs(length(uv-vec2(-0.6,.03))-.09));
n += (1.-S(-.6,uv.x)+S(-.05,uv.y))*S(.05,abs(length(uv-vec2(-0.6,-.04))-.09));
return n;
}

#define TAU 6.2831853072

// http://marcodiiga.github.io/radial-lens-undistortion-filtering
vec2 barrelDistort(in vec2 p, in vec2 alpha) {
    return p / (1.0 - alpha * dot(p, p));
}

// 1D noise
float Hash11(in float p) { // https://www.shadertoy.com/view/4djSRW
    p = fract(p * 0.1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

float snoise11(in float p) {
    return mix(Hash11(floor(p)), Hash11(ceil(p)), smoothstep(0.0, 1.0, fract(p)));
}

float fbm11(in float p) {
    p *= 6.0;

    float res = 0.0;
    float amp = 1.0;
    float totAmp = 0.0;
    for (int oct=0; oct < 5; oct++) {
        res += amp * snoise11(p);
        totAmp += amp;
        p *= 2.0;
        amp *= 0.5;
    }

    return res / totAmp;
}

// 2D noise
float Hash21(in vec2 p) { // https://www.shadertoy.com/view/4djSRW
    vec3 p3 = fract(p.xyx * 0.1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float snoise21(in vec2 p) {
    vec2 cell = floor(p);
    vec2 local = smoothstep(0.0, 1.0, fract(p));

    float bl = Hash21(cell);
    float br = Hash21(cell + vec2(1.0, 0.0));
    float tl = Hash21(cell + vec2(0.0, 1.0));
    float tr = Hash21(cell + 1.0);

    return mix(mix(bl, br, local.x), mix(tl, tr, local.x), local.y);
}

float fbm21(in vec2 p) {
    float res = 0.0;
    float amp = 1.0;
    float totAmp = 0.0;
    for (int oct=0; oct < 5; oct++) {
        res += amp * abs(2.0 * snoise21(p) - 1.0);
        totAmp += amp;
        p *= 2.0;
        amp *= 0.5;
    }

    return res / totAmp;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 4.0;
    vec2 mouse = (iMouse.xy - center) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    vec3 color = vec3(1.0);

    // Symmetry (not mirroring though)
    uv.x = uv.x - 1.4 * sign(uv.x);

    // Distorted UVs to fake a 3D look
    vec2 bulgeUv = barrelDistort(uv, vec2(0.25));
    vec2 eyeballUv = bulgeUv - 0.15 * mouse; // Look at mouse

    // Veins
    float co = cos(1.4), si = sin(1.4);
    mat2 rot = mat2(co, si, -si, co);

    float veinFade = smoothstep(1.8, 0.0, length(eyeballUv));
    for (int i=0; i < 4; i++) {
        color.gb -= 0.7 * smoothstep(0.2, 0.0, fbm21(2.0 * eyeballUv - 10.0)) * veinFade;
        eyeballUv *= rot;
    }

    color = max(color, 0.0);

    // Iris
    vec2 polar = vec2(atan(eyeballUv.y, eyeballUv.x), length(eyeballUv));

    float irisWidth = 0.125 + 0.015 * snoise11(iTime);
    float irisRadius = 0.375 - irisWidth;
    float irisDist = abs(length(eyeballUv) - irisRadius) - irisWidth;

    vec3 irisColor = vec3(0.7, 0.4, 0.0) * fbm11(polar.x);
    irisColor += vec3(0.0, 0.45, 0.0) * fbm11(polar.x - 10.0);
    color = mix(color, irisColor * 0.9, smoothstep(unit, 0.0, irisDist));
    //color *= smoothstep(0.0, 0.31, polar.y);
    if (polar.y < irisRadius + irisWidth) color *= smoothstep(0.1, -0.1, irisDist); // It took me a while to realize this was what I really needed!

    // Pupil
    color = max(color - smoothstep(unit, 0.0, polar.y - irisRadius + irisWidth), 0.0);

    // Fake specular highlight
    color += smoothstep(0.2, -0.2, length(uv - vec2(0.3, 0.15)));

    // Eye shape (deformed circle)
    vec2 eyeUv = vec2(uv.x, 3.5 * uv.y / (2.0 - uv.x * uv.x));
    //vec2 eyeUv = vec2(uv.x, 6.5 * uv.y / (4.0 - uv.x * uv.x));

    float eyeMask = smoothstep(0.1, -0.1, length(eyeUv) - 1.0); // Outline
    eyeMask *= smoothstep(2.0, 0.0, length(eyeUv)); // Shading

    float blink = 1.0 - 2.0 * pow(abs(cos(mod(iTime, TAU))), 1000.0);
    float eyelidCurve = 0.15 * bulgeUv.x * bulgeUv.x * blink;
    eyeMask *= smoothstep(0.1, 0.0, bulgeUv.y + eyelidCurve - blink); // Eyelid

    color *= eyeMask;

    fragColor = vec4(color, 1.0);
}

