#define initialPos vec3(0.0,1.0,0.0) //starting position
#define initialRot vec3(0.0)
#define mouseSensitivity 0.8
#define plrHeight 0.5 
#define gravity 2.0 
#define moveSpeed 1.8 

#define wkey 87.0
#define akey 65.0
#define skey 83.0
#define dkey 68.0
#define spacekey 32.0

//detects keypresses
bool keyDown(float keyCode) {
    keyCode = (keyCode + 0.5) / 256.0;
    vec2 uv = vec2(keyCode, 0.25);
    return texture(iChannel0, uv).x > 0.0;
}

/*
 * position is stored at pixel 0,0
 * rotation is stored at pixel 0,1
 * rotation before mouse click is stored at pixel 0,2
 */

vec4 getPosition(){
    vec4 p = texelFetch(iChannel1, ivec2(0,0), 0) * 255.0 - 100.0;    
    return p.xyzw;
}

vec3 getRotation(){
   vec4 r = texelFetch(iChannel1, ivec2(0,1), 0) * 255.0 - 100.0;     
   return r.xyz;
}

vec3 getPRotation(){
   vec4 r = texelFetch(iChannel1, ivec2(0,2), 0) * 255.0 - 100.0;     
   return r.xyz;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){ 
    //discard unused fragments
    if(int(fragCoord.x) > 0 || int(fragCoord.y) > 2){
        discard;
    }

    if(iFrame > 0){ 
        vec3 rot = getRotation();

        //handle movement and position
        switch(int(fragCoord.y)){
            case 0:
                vec4 posb = getPosition();
                vec3 pos = posb.xyz;

                raycastReturn collide = raycast(Ray(vec3(pos.x, pos.y - 0.1, pos.z), normalize(vec3(1.0,-1.0,1.0))));

                //gravity
                if(collide.dist > plrHeight && posb.w >= 0.0){
                    pos.y -= gravity * posb.w * iTimeDelta;
                    posb.w += 0.02; //slowly accelerate fall
                }else if(posb.w > 0.0){
                    posb.w = 0.0; //reset w 
                }

                //reset fall
                if(pos.y < -4.0){
                    pos = initialPos;
                }

                if(keyDown(wkey)){ 
                    pos = pos + vec3(0.0,0.0,1.0) * rotate(vec2(0.0,rot.y)) * moveSpeed * iTimeDelta;
                } 

                if(keyDown(skey)){
                    pos = pos + vec3(0.0,0.0,-1.0) * rotate(vec2(0.0,rot.y)) * moveSpeed * iTimeDelta;
                } 

                if(keyDown(akey)){
                    pos = pos + vec3(-1.0,0.0,0.0) * rotate(vec2(0.0,rot.y)) * moveSpeed * iTimeDelta;
                } 

                if(keyDown(dkey)){
                    pos = pos + vec3(1.0,0.0,0.0) * rotate(vec2(0.0,rot.y)) * moveSpeed * iTimeDelta;
                } 

                if(keyDown(spacekey) && collide.dist <= plrHeight){
                    posb.w = -1.0; //start a jump if space is pressed
                }

                if(posb.w < 0.0){
                    pos.y += 0.04 * -posb.w;
                    posb.w += gravity * iTimeDelta;
                }

                fragColor = vec4((pos + 100.0) / 255.0, (posb.w + 100.0) / 255.0);
            break;

            case 1:
                if(iMouse.z > 0.0){
                    rot = getPRotation() - vec3(((iMouse.xy-abs(iMouse.zw)) * vec2(-1.0,1.0)).yx * mouseSensitivity, 0.0);
                    rot.x = clamp(rot.x, -100.0, 100.0); //prevent camera from flipping
                }

                fragColor = vec4((rot + 100.0) / 255.0, 1.0);
            break;

            case 2: 
                if((iMouse.z > 0.0) == false){
                    fragColor = vec4((rot + 100.0) / 255.0, 1.0);
                }else{
                    fragColor = vec4((getPRotation() + 100.0) / 255.0, 1.0);
                }
            break;
        } 
    }else{
        //initialize
        switch(int(fragCoord.y)){
            case 0:
                fragColor = vec4((initialPos + 100.0) / 255.0, 100.0 / 255.0);
            break;

            case 1:
                fragColor = vec4((initialRot + 100.0) / 255.0, 1.0);
            break;

            case 2:
                fragColor = vec4((initialRot + 100.0) / 255.0, 1.0);
            break;
        }
    }
}

