vec3 sky(vec3 raydir){ //very crappy sky
    vec3 col;
    if(raydir.y > 0.0){
        float i = max(1.0-dot(vec3(0.0,1.0,0.0), raydir*30.0),0.0);
        col = vec3(0.271, 0.345, 0.482)+i+(1.0-raydir.y)*0.48;
    }else{
        float i = max(1.0-dot(vec3(0.0,1.0,0.0), -raydir*30.0),0.0);
        col = vec3(0.416, 0.396, 0.382)+i+raydir.y*0.1;
    }

    return clamp(col, 0.0, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord/iResolution.xy*2.0-1.0;
    uv.x*=iResolution.x/iResolution.y;

    vec2 js[4];
    js[0] = vec2(0.25,0.75);
    js[1] = vec2(0.75,-0.25);
    js[2] = vec2(-0.25,-0.75);
    js[3] = vec2(-0.25,0.75);

    //get camera position from Buffer A texture
    vec3 origin = getPosition();

    vec3 color;
    for(int i=0; i<js.length(); i++){
        vec3 dir = normalize(vec3(uv+(js[i]/iResolution.xy),1.0))*rotate(getRotation()); //rotate view rays
        Ray ray = Ray(origin, dir);
        raycastReturn rcast = raycast(ray); //cast ray into scene

        if(length(rcast.normal) > 0.0){ //check ray collision
            color += rcast.color*max(dot(sunDirection, rcast.normal),0.5); //perform basic shading
        }else{
            color += sky(ray.direction) + 0.2;
        }
    }
    color/=float(js.length());

    fragColor = vec4(color,1.0);
}

