    float sds1=sdSphere(p-vec3(0.0,5.0,0.0), 5.0 );
    float d2= sdBox(p-vec3(-10.0,5.0,-5.0), vec3(3.0,10.0,2.0) );
    float d3= sdBox(p-vec3(10.0,15.0,5.0), vec3(3.0,15.0,5.0) );
    float d4=sdSphere(p-vec3(0.0,14.0,0.0), 3.0 );
    float d5= sdBox(p-vec3(-10.0,3.5,10.0), vec3(2.0) );

    res =opU2(res, vec2(sds1,2.0));
    res =opU2(res, vec2(d2,3.0));
    res =opU2(res, vec2(d3,4.0));
    res =opU2(res, vec2(d4,5.0));
    res =opU2(res, vec2(d5,6.0));
    return res;
}

// basic trace, with some LOD
vec2 RayMarch(vec3 ro, vec3 rd, float m) {
    float t;
	vec2 d = vec2(9999.0,-1.0);

	for(int i = 0; i < 200; i++) {
		d = GetDist(ro + rd*t);
		if(d.x < (0.001 + 0.0001*t) || t >= m) break;
        t += d.x*0.67;
	}
	return vec2(t,d.y);
}

// basic normal.
vec3 normal(vec3 p) {
	vec2 h = vec2(0.001, 0.0);
	vec3 n = vec3(
		GetDist(p + h.xyy).x - GetDist(p - h.xyy).x,
		GetDist(p + h.yxy).x - GetDist(p - h.yxy).x,
		GetDist(p + h.yyx).x - GetDist(p - h.yyx).x
	);
	return normalize(n);
}

float getSoftShadow(vec3 p, vec3 lightPos)
{   float res = 1.0;
    float dist = 0.01;
    float lightSize = 0.03;
    for (int i = 0; i < 15; i++) {
        float hit = GetDist(p + lightPos * dist).x;
        res = min(res, hit / (dist * lightSize));
        dist += hit;
        if (hit < 0.0001 || dist > 60.0) break;
    }
    return clamp(res, 0.0, 1.0);
}

float ambOcclusion(vec3 pos, vec3 nor)
{   float sca = 2.0, occ = 0.0;
    for(int i = 0; i < 10; i++) {
        float hr = 0.01 + float(i) * 0.5 / 4.0;
        float dd = GetDist(nor * hr + pos).x;
        occ += (hr - dd)*sca;
        sca *= 0.6;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );
}

//euclidean cameras.
mat3 camera(vec3 o, vec3 l) 
{	vec3 w = normalize(l - o);
	vec3 u = normalize(cross(vec3(0, 1, 0), w));
	vec3 v = normalize(cross(w, u));	
	return mat3(u, v, w);
}

vec3 iluminacion(vec3 pos,vec3 rd,vec3 nor, vec3 ref, vec3 lig, float id_color )
{
    vec3 rcol =vec3(0.0);    
    // occlusion and shadows
    float occ = ambOcclusion(pos, nor);
	//float sha = step(5.0, trace(pos + nor*0.001, lig, 5.0));
    float sha = getSoftShadow(pos, normalize(lig));

    // lighting ambient + diffuse + fresnel + specular
	rcol += 0.2*occ;
	rcol += clamp(dot(lig, nor), 0.0, 1.0)*occ*sha;
	rcol += pow(clamp(1.0 + dot(rd, nor), 0.0, 1.0), 2.0)*occ;
	rcol += 2.0*pow(clamp(dot(ref, lig), 0.0, 1.0), 30.0)*occ;


    if(id_color==1.0)
    {    rcol *= 0.2 + 0.5*mod(floor(pos.x*0.25) + floor(pos.z*0.25), 2.0);}

	if(id_color==2.0)
    { rcol *= vec3(0.2, 0.7, 0.7);  }

     if(id_color==3.0)
    { rcol *= vec3(1.0, 0.32, 0.17); }

    if(id_color==4.0)
    { rcol *= vec3(1.0); }

    if(id_color==5.0)
    { rcol *= vec3(1.0,0.0,0.0); }

    if(id_color==6.0)
    { rcol *= vec3(1.0,1.0,0.0); }


	return rcol;		
}        

vec3 render(vec3 ro, vec3 rd) 
{
    vec3 col = vec3(0.45, 0.8, 1.0);
	vec3 lig1 = normalize(vec3(10.8, 20.0, -25.6));
    vec3 lig2 = normalize(vec3(0.0, 10., 25.6));
    vec2 t;

    	for(int i = 0; i < 3; i++) {
		 t = RayMarch(ro, rd, 50.0);
		if(t.x < 50.0) 
        {
			vec3 rcol = vec3(0);
            // geometry, hit position, normal, reflect
			vec3 pos = ro + rd*t.x;
			vec3 nor = normal(pos);
			vec3 ref = reflect(rd, nor);
            rcol=iluminacion(pos,rd, nor, ref, lig1,t.y);
            rcol+=iluminacion(pos,rd, nor, ref, lig2,t.y);
            rcol/=2.0;

            // set up the ray orgin and direction for reflection.

            ro = pos + nor*0.001;
            //separacion de colores y reflection
            if (t.y<5.0)  
			rd = ref;

            // sky fog.
			rcol = mix(rcol, vec3(0.45, 0.8, 1.0), 1.0 - exp(-0.00715*t.x));
            // lighten intensity on each successive reflect.
			if(i == 0)
               col = rcol;
			else
                col *= mix(rcol, vec3(1), 1.0 - exp(-0.8*float(i)));
		}
	}
    return col;
}


vec3 linear2srgb(vec3 c) 
{ return mix(12.92 * c,1.055 * pow(c, vec3(1.0/1.8)) - 0.055, step(vec3(0.0031308), c)); }

vec3 exposureToneMapping(float exposure, vec3 hdrColor) 
{ return vec3(1.0) - exp(-hdrColor * exposure); }


vec3 ACESFilm(vec3 x)
{   float a,b,c,d,e;
    a = 2.51; b = 0.03; c = 2.43; 
    d = 0.59; e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

Ray RotarEnCirculo(vec3 ro,vec3 rd)
{
   float r=25.0;
   float veltime=30.0;  
    float tt = radians( -iTime*veltime);
    vec3  pos=vec3(0.0,0.0,20.0);
    vec3 rotation1 = pos+vec3(r*sin(tt), 0.0, r*cos(tt));
    ro +=rotation1;   
    rd=rotate_y( vec3(-rd.x,rd.y,-rd.z), tt);
    return  Ray(ro,rd);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{	vec2 p = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro=vec3(0.0,8.0,-15.0);
    vec3 rd=normalize(vec3(p,1.0));

     Ray ray= RotarEnCirculo(ro,rd);
    ro=ray.ro;
    rd=ray.rd;


    vec3 col= render(ro, rd);

    // tone mapping and gamma correction.
	//col = 1.0 - exp(-0.5*col);
	//col = pow(abs(col), vec3(1.0/2.2));
	col= (ACESFilm(col)+linear2srgb(col)+col+ exposureToneMapping(3.0, col))/4.0 ;
	fragColor = vec4(col, 1);
}

