#define pi 3.14159

float h21 (vec2 a, float zoom) {
    a = mod(a, zoom);
    return fract(sin(dot(a, vec2(12.9898, 78.233)))*43758.5453123);
}

float rand(vec2 ipos, float num) {
    float val = h21(ipos, num) + 0.4 * iTime;

    float a = 2. * pi * h21(ipos, num);
    float c = cos(a), s = sin(a);

    float f = floor(val);
    vec2 v = vec2(c,s);
    float v1 = h21(f * v + 0.01 * ipos, num);
    float v2 = h21((f + 1.) * v + 0.01 * ipos, num);  

    float m = smoothstep(0., 1., fract(val));

    return mix(v1, v2, m);
}

vec2 movingTiles(vec2 uv, float sc, float spd){
    float time = spd * iTime;

    float ft = fract(2. * (abs(uv.x) + abs(uv.y)) + time);

    uv *= sc;

    float k = step(0.5, ft);//smoothstep(-0.,0.5, ft - 0.5);
    uv.x +=      k * sign(fract(uv.y * 0.5) - 0.5) * ft * 2.;
    uv.y += (1.-k) * sign(fract(uv.x * 0.5) - 0.5) * ft * 2.;

    return fract((2. + 0.03 * h21(uv)) * uv);
}

float grid(vec2 uv, float sc) {
    // Cut uv into smaller uvs
    vec2 ipos = floor(sc * uv);
    vec2 fpos = fract(sc * uv);   

    float num = 301.; // can be anything

    // Generate values for each corner of uv, used for circle radii
    float l  = rand(ipos + vec2(1,0), num);
    float t  = rand(ipos + vec2(0,1), num);
    float tl = rand(ipos + vec2(1,1), num);
    float id = rand(ipos + vec2(0,0), num);

    // Smooth the cut uvs so different uvs meet continuously on the edges
    //uv = uv * uv * (3. - 2. * uv);
    //fpos = smoothstep(0., 1., fpos);
    fpos = fpos * fpos * (3. - 2. * fpos);

    // Box lerp between the corner values to get a radius value for this pixel
    float v = l * fpos.x * (1.-fpos.y)
     	    + t * (1.-fpos.x) * fpos.y
     	    + tl * fpos.x * fpos.y
      	    + id * (1.-fpos.x) * (1.-fpos.y);
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float sc = 10.;

    float t = 0.2 * iTime;

    float m = 0.5;
    vec2 uv2 = uv;
    float v = 0.;
    for (float i = 0.; i < 5.; i++) {

        t -= 2. * uv.x; //length(uv);

        v += m * grid(uv2, sc);
        float cs = cos(t), sn = sin(t);
        mat2 rot = mat2(cs, -sn, sn, cs);
        uv2 *= rot;
        m *= 0.35;
    }

    // Do the tile pattern
    vec2 fpos = movingTiles(uv, sc, -0.1) - 0.5;

    // Generate circle using radius we've obtained
    float d = 0.8-mlength(fpos);
    float k = 10. / iResolution.y;
    float s = smoothstep(-k, k, -d + 0.68 * v);
    vec3 col = vec3(s);

    //col += (1.-s) * (0.35 + .65 * v) * vec3(0.5,0.5,1.);
    vec3 e = vec3(1.);
    col += (1.-s) * pal(0.43 + 0.15 * v, e, e, e, 0.45 * vec3(0,1,2)/3.);
    col = clamp(col, 0., 1.);
    col += s * vec3(fpos, 1.);

    fragColor = vec4(col,1.0);
}

