mat3 orthBasis( vec3 z ) {
  z = normalize( z );
  vec3 up = abs( z.y ) > 0.999 ? vec3( 0.0, 0.0, 1.0 ) : vec3( 0.0, 1.0, 0.0 );
  vec3 x = normalize( cross( up, z ) );
  vec3 y = cross( z, x );
  return mat3( x, y, z );
}


vec3 cyclicNoise( vec3 p ) {
  vec3 sum = vec3( 0.0 );
  float amp = 0.52;
  float warp = 0.6;
  mat3 rot = orthBasis( vec3( 0.5,-.5,.2 ) );

  for ( int i = 0; i < 4; i ++ ) {
    p *= rot * 2.0;
    p += sin( p.zxy * warp );
    sum += sin( cross( cos( p ), sin( p.yzx ) ) ) * amp;
    amp *= 0.5;
    warp *= 1.3;
  }

  return sum;
}

mat2 _rot(float a){
    return mat2(cos(a),-sin(a),sin(a),cos(a) );
}

vec4 cyclicNoise( vec4 p ) {
  vec4 sum = vec4( 0.0 );
  float amp = 0.5;
  float warp = 1.1;

  p.xw *= _rot(1.5);
  p.wy *= _rot(.9);

  for ( int i = 0; i < 5; i ++ ) {

    p += sin( p.wzxy * warp );
    sum += sin( dot( cos( p ), sin( p.yzwx ) ) ) * amp;
    amp *= 0.5;
    warp *= 1.3;
    p.xz *= _rot(0.9);
    p.xy *= _rot(4.9);
  }

  return sum;
}


vec3 getNoise(vec2 uv){
    vec3 col = vec3(0);
    vec3 na = cyclicNoise(vec3(uv + 20.,5.));


    vec3 n = cyclicNoise(
        vec3(
            uv*10. + na.xy*4.,
            cyclicNoise(vec3(uv,1.)).x*4.
        )
    );
    vec3 nb = cyclicNoise(
        vec3(
            uv*2. - n.xy*1.,
            cyclicNoise(vec3(n.xy,n)).x*-.2  - 10.
        )
    );

    vec3 nc = cyclicNoise(
        vec3(
            uv*22. - n.xy*1. + nb.xz*1.,
            n.y*1. + nb.x - n.x
        )
    );

    vec3 nd = cyclicNoise(
        vec3(
            n.xy*2. + nc.xz*0.6,
            nc.y + 5.
        )
    );
    vec3 ne = cyclicNoise(
        vec3(
            nd.xy*2. + uv.xy,
            nd.x*1. + 441.
        )
    );
    //ne = max(ne,0.8);

    n *= nb*7.*dot(nc,vec3(0.2 - nd.x,1. - nd.y,0.1))*nd*ne*3.;

    n = dot(n,vec3(0.23,1,0.5))*vec3(1.);

    n = max(n,0.);

    n = n/(1.+n);

    n = pow(n,vec3(4.1))*55.;
    return n;
}


