    return min(d,c2);
}

vec3 normal(vec3 p){
    vec2 e=vec2(0,.001);
    float a=map(p);
    return(normalize(a-vec3(map(p-e.yxx),
                          map(p-e.xyx),
                          map(p-e.xxy))));
}




mat3 cam(vec3 ro, vec3 lp) {
	vec3 cd = normalize(lp - ro); 
	vec3 cr = normalize(cross(vec3(0, 1, 0), cd)); 
	vec3 cu = normalize(cross(cd, cr));

	return mat3(-cr, cu, -cd);
}

vec3 normalmap(vec2 p){
    float v=texture(iChannel1,p).r;
    float vx=v-texture(iChannel1,p-vec2(0.001,0)).r;
    float vy=v-texture(iChannel1,p-vec2(0.,0.001)).r;
    return normalize(vec3(vx,0.,vy));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 ro=vec3(0.,1.,3.);

    //ro.xz*=mat2(cos(T),sin(T),-sin(T),cos(T));
    vec3 rd=normalize(cam(ro,vec3(0.,0.,0.))*normalize(vec3(uv,-1)));
    float dt=0.;
    for(float i=0.;i<200.;i++){
        float d=map(ro+dt*rd);
        dt+=d;
        if(d<.001 || d > 200.)
            break;
    }
    float dust=0.;
    vec3 pos=ro;

    //there must be a better solution...
    for(float i=0.;i<20.;i++){
          float s=gyro(normalize(pos));

           s*=50.*smoothstep(.8,1.,length(pos));
           s=clamp(0.,1.,s);
            dust+=s*(1./max(1.,(length(pos))));
            dust+=pow(smoothstep(.4,.001,length(pos))*10.,3.);
        pos+=rd;
    }
    if(dt<200.){
    vec3 lpos=vec3(0,0,0);

        vec3 p=ro+dt*rd;
        if(!isgyro){
                    vec3 n=normal(p);
            float s=gyro(normalize(-p));
            float w=.04*length(p/3.);
            s=smoothstep(-w,w*2.,s)*.3;
            float diff=dot(n,normalize(lpos-p));
            vec3 r=vec3(1.);//texture(iChannel0,normalize(reflect(rd,n))).xyz;
            //fragColor=vec4(r*s/(dt),1.);
            fragColor=vec4((vec3(.4,.8,.9)*diff*s),1.);
        }
        else{

        vec3 n=normal(p);
        float diff=dot(n,normalize(lpos-p));
        vec3 colext=vec3(.2,.3,.9)*texture(iChannel0,normalize(reflect((rd),n))).xyz*.2;

        vec3 colint=vec3(.95,.2,.1)*clamp((diff),.0,1.);
        if(diff<0.){
                    //dust=0.;

            }
            dust=dust*smoothstep(-.4,.4,diff);
           fragColor=vec4(mix(colext,colint,smoothstep(-.2,.2,diff)),1.);
        }
    }
      else      fragColor = vec4(vec3(0.),1.0);

   // fragColor*=4.;
    fragColor=pow((fragColor+vec4(dust*.05)*vec4(.9,.7,.7,1.)),vec4(.45));

 //   fragColor=vec4(dust/10.);
}

