void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    // Spiral
    vec2 center = vec2(iResolution.x/2.0, iResolution.y/2.0);
    float dist = distance(center, fragCoord);
    float angle = atan(fragCoord.y - iResolution.y/2.0, fragCoord.x - iResolution.x/2.0);
    float col = cos(0.25 * dist + angle + 4.0 * iTime);

    // Fade
    float distToEdge = distance(center, vec2(iResolution.x/2.0, iResolution.y));
    float percentDistToEdge = clamp(dist / distToEdge, 0.0, 1.0);
    col = mix(0.0, col, 1.0 - percentDistToEdge);

    // Return
    fragColor = vec4(vec3(col), 1.0);

}

//change these constants to get different patterns!
#define iterations 3
#define modulo 3


#define c1 vec3(1.,0.5,1.5)

vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 8.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float scale = c1.z;
    vec2 t2 = vec2(0.);
    vec2 t3 = vec2(0.);   
    for(int k = 0; k < 6*iterations; k++){
    uv -= (t2.yx)/(scale)+float(k%modulo);
    //uv -= t3.yx/(8.);
    t2 = triangle_wave(uv.yx-.5,scale);
    t3 = -triangle_wave(uv,scale);
    //t3 /= float(1+k%3);
    uv.yx = -(t2+t3)/scale;
    col.x = 1.-abs(-uv.y+uv.x+col.x);
    col = col.yzx;
    uv /= scale*scale;
    }
    fragColor = vec4(col,1.0);
}

#define WALKSPEED 4.
#define color
#define sdTest sdGiant
mat3 rotx(float a) { mat3 rot; rot[0] = vec3(1.0, 0.0, 0.0); rot[1] = vec3(0.0, cos(a), -sin(a)); rot[2] = vec3(0.0, sin(a), cos(a)); return rot; }
mat3 roty(float a) { mat3 rot; rot[0] = vec3(cos(a), 0.0, sin(a)); rot[1] = vec3(0.0, 1.0, 0.0); rot[2] = vec3(-sin(a), 0.0, cos(a)); return rot; }
mat3 rotz(float a) { mat3 rot; rot[0] = vec3(cos(a), -sin(a), 0.0); rot[1] = vec3(sin(a), cos(a), 0.0); rot[2] = vec3(0.0, 0.0, 1.0); return rot; }

float smin( float a, float b, float k )
{
    float h = a-b;
    return 0.5*( (a+b) - sqrt(h*h+k) );
}

float sdSphere(vec3 p, float r){
    return length(p) - r;
}


float sdVerticalCapsule( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}

vec4 texcube( sampler2D sam, in vec3 p, in vec3 n )
{
    vec3 a = n*n;
	vec4 x = texture( sam, p.yz );
	vec4 y = texture( sam, p.zx );
	vec4 z = texture( sam, p.yx );
	return (x*a.x + y*a.y + z*a.z) / (a.x + a.y + a.z);
}

vec3 opElongate(in vec3 p, in vec3 h )
{
    vec3 q = p - clamp( p, -h, h );
    return q;
}


vec3 opCheapBend( in vec3 p, float k)
{

    float c = cos(k*p.x);
    float s = sin(k*p.x);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*p.xy,p.z);
    return q;
}
float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdTorus( vec3 p, float t, float r )
{
  vec2 q = vec2(length(p.xz)-r,p.y);
  return length(q)-t;
}
float sdHexPrism( vec3 p, vec2 h )
{
  const vec3 k = vec3(-0.8660254, 0.5, 0.57735);
  p = abs(p);
  p.xy -= 2.0*min(dot(k.xy, p.xy), 0.0)*k.xy;
  vec2 d = vec2(
       length(p.xy-vec2(clamp(p.x,-k.z*h.x,k.z*h.x), h.x))*sign(p.y-h.x),
       p.z-h.y );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float smax( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h); }

vec3 opSymX( in vec3 p)
{
    p.x = abs(p.x);
    return p;
}
vec3 opSymZ( in vec3 p)
{
    p.z = abs(p.z);
    return p;
}

vec3 opSymXY(vec3 p){
    p.xy = abs(p.xy);
    return p;
}

float sdXCapsule( vec3 p, float h, float r )
{
  p.x -= clamp( p.x, 0.0, h );
  return length( p ) - r;
}

float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdEllipsoid( vec3 p, vec3 r )
{
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

float sdRoundedBox( in vec2 p, in vec2 b, in vec4 r )
{
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}

float sdEquilateralTriangle( in vec2 p )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - 1.0;
    p.y = p.y + 1.0/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0, 0.0 );
    return -length(p)*sign(p.y);
}


float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0)-0.5;
}

float sdBBox(vec3 p, vec3 b){
    float d = sdBox(p.xz, b.xz);
    vec2 w = vec2( d, abs(p.y) - b.y );
    return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

float sdRoundBox( in vec3 p, in float h )
{
    float d = sdRoundedBox(p.xy, vec2(0.35,0.08), vec4(0.05,0.15,0.05,0.1));
    vec2 w = vec2( d, abs(p.z) - h );
    return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

float sdTriPrisim( in vec3 p, in float h )
{
    float d = sdEquilateralTriangle(p.xy);
    vec2 w = vec2( d, abs(p.z) - h );
    return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

float pow4(float v){
    return v*v*v*v;
   }

float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); }

float sdBeardSpine(vec3 p, float s){
    p*=s;
    return min(opSmoothSubtraction(sdSphere(p-vec3(0,-8.90,1.6), 10.),
                                sdTriPrisim(p-vec3(0,sin(p.z)*0.9,1.), 1.),
                                0.4)/s,
                                1.);
}

float sdBeltBuckle(vec3 p){
    return sdHexPrism(p-vec3(0, -1.5, -1.), vec2(.35,.1));
}

float sdBelt(vec3 p){
    return min(
                sdTorus(opElongate(p-vec3(0, -1.5, 0), vec3(1., 0.2, 0.1)), 0.1, .6),
                sdBeltBuckle(p)
                );
}

vec3 repeat4y(vec3 p){
    const float l = 2.;
    const float c = 0.3;
    float q = p.y-c*clamp(round(p.y/c),-l,l);
    return vec3(p.x, q, p.z);
}

float sdBeard(vec3 p){
    vec3 q = opSymX(p)-vec3(.6,-1.4,-.4);
    vec3 w = opSymX(p)-vec3(1.5,-1.2,-.4);
    vec3 z = opSymX(p)-vec3(1.15,-0.5,-.0);
    return min(min(
                    sdBeardSpine(q*rotz(-0.1*3.14)*roty(.1*3.14)*rotx(.3*3.14),2.3),
                    sdBeardSpine(w*rotz(-0.2*3.14)*roty(.3*3.14)*rotx(.2*3.14),2.)),
                    sdBeardSpine(z*roty(0.3*3.14)*rotz(-0.25*3.14)*rotx(.2 * 3.14),4.) 
                );
}

float sdMouthCavity(vec3 p){

    return sdRoundBox(p-vec3(0,-0.4,-0.4), .5);
}

float sdCoil(vec3 p){
    return sdTorus(opElongate(repeat4y(p-vec3(1.7,-0.7,0)), vec3(0.1,0.,.8)), 0.2, 0.5);
}

float sdPlate(vec3 p){
    p-=vec3(0,0.1,-0.085);
    p*=roty(0.5*3.141);
    vec3 symP = opSymZ(p);
    return  min(
                smin(
                    max(
                        -sdHexPrism((p-vec3(0.,-0.2,.20)),vec2(1.2, 2.7))+0.2,
                        max(
                            max(
                                -sdHexPrism(opElongate(p*rotx(0.5*3.141)+vec3(0.3,0,0.1), vec3(0.,0.4,0)), vec2(0.4,2.7))+0.2,
                                sdHexPrism(p,vec2(1.2, 2.7))-0.1),
                            sdHexPrism(p*rotx(0.5*3.141),vec2(1.5, 1.8))-0.1
                            )
                        ),
                    min(sdCapsule(symP, vec3(0.5,0,0.5), vec3(0.9,1.4,.9)*1.5, 0.12),
                        sdSphere(symP-vec3(0.9,1.4,.9)*1.4, 0.54)
                        ),
                    0.02),
                    min(
                        max(-sdBox(p-vec3(1.7,-0.7,0), vec3(.9,0.7,1.9)),
                            sdBBox(p-vec3(1.7,-0.7,0), vec3(.3,1,1.1))),
                        sdCoil(p))
                    );
}

float sdRoundCone( vec3 p, float r1, float r2, float h )
{
  // sampling independent computations (only depend on shape)
  float b = (r1-r2)/h;
  float a = sqrt(1.0-b*b);

  // sampling dependant computations
  vec2 q = vec2( length(p.xz), p.y );
  float k = dot(q,vec2(-b,a));
  if( k<0.0 ) return length(q) - r1;
  if( k>a*h ) return length(q-vec2(0.0,h)) - r2;
  return dot(q, vec2(a,b) ) - r1;
}

float sdBand(vec3 p){
    return sdTorus(opElongate((p-vec3(0,0.16,0))*rotx(0.1), vec3(0.1,0.047,0.1)), 0.05, 0.62);
}

float sdTeeth(vec3 p){
 return sdTorus(p-vec3(0,-0.4,-0.25+sin(p.x*40.)*0.01), .11,.3);
}

float sdLens(vec3 p){
    return sdCappedCylinder(p, 0.29, 0.35);
}

float sdRim(vec3 p){
    p = opSymX(p);
    p-=vec3(0.43,0.05,-0.4);
    p*=rotx(1.44);
    return min(
                max(-sdCappedCylinder(p, 0.2, 10.),sdCappedCylinder(p, 0.29, 0.35)-0.03),
                sdLens(p)
               );
}

float sdGoggles(vec3 p){
    return min(sdBand(p),
               sdRim(p));
}

float sdNose(vec3 p){
       p -= vec3(0,-.2,-.7);
       return opSmoothSubtraction(sdSphere(opSymX(p)-vec3(0.3,-0.3,-0.1), 0.33),
                                   sdRoundCone(p, 0.2, 0.07, 0.2),
                                   0.1);
}

float sdHexagram( in vec2 p, in float r )
{
    const vec4 k = vec4(-0.5,0.8660254038,0.5773502692,1.7320508076);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= 2.0*min(dot(k.yx,p),0.0)*k.yx;
    p -= vec2(clamp(p.x,r*k.z,r*k.w),r);
    return length(p)*sign(p.y);
}
float gloveRing(vec3 p, float h, float r){
    float d = sdHexagram(p.xy, r);
    vec2 w = vec2( d, abs(p.z) - h );
    return min(max(w.x,w.y),0.0) + length(max(w,0.0))-0.1;
}
float sdGlove(vec3 p){
    p -= vec3(.15, -2.2, -2.2);
    p *= 3.;
    return smin(
                smin(
                    smin(
                        smin(
                            sdTorus(repeat4y(opElongate(p, vec3(0.1,0,0.1))), 0.28, 0.4),
                            sdBox(p-vec3(0.2,0.1,1.0),vec3(0.1, .3, 0.6))-0.5,
                            0.5),
                        sdCapsule(p, vec3(-0.9,.5,0.), vec3(-0.8,-.5,0.), 0.5),
                        0.01),
                    sdCapsule(p, vec3(-0.5,.8,1.), vec3(-0.9,.5,0.), 0.5),0.6),
                 gloveRing(p-vec3(0,0,2.6), .2, 1.),
                 0.2) / 3.;

}

float sdShoulder(vec3 p){
    p = opSymX(p)-vec3(2.,0.6,0);
    return sdEllipsoid(p,vec3(0.9, 0.8, 0.7));
}

float sdArm(vec3 p){
    float x = p.x;
    p = opSymX(p)-vec3(2.,0.6,0);
    p*= roty(sin(iTime*WALKSPEED)*x/abs(x)*0.3-0.25)*rotz(sin(iTime*WALKSPEED)*x/abs(x)*0.3-0.25)*rotx(sin(iTime*WALKSPEED)*x/abs(x)*0.3-0.25);
    return min(min(
                smin(
                    sdEllipsoid(p-vec3(0,-0.8,-0.1),vec3(0.5, 0.8, 0.6)),
                    sdEllipsoid((p-vec3(0.1,-2.,-1.1))*rotx(0.2*3.14), vec3(0.5,0.4,.6)),
                    0.3),
                 sdGlove(p)
               ),2.1);
               //Quick hack to prevent the ray from overshooting when evaluating the other side
               //Since the left arm is just a mirror of the right if x>0 then the ray will get the
               //distance for the right arm and could over shoot the left one.
               //Clamping the distance to a max of 2 will prevent this overshooting
}

float sdHead(vec3 p){
    p-=vec3(0,1.8,-0.5);
    vec3 q = vec3(p.x, p.y+cos(p.x*4.)*0.07-0.05+p.y*p.y*0.1, p.z-0.03);
    float bound = sdSphere(q, 1.);
    if (bound < 1.) {
    return smin(
                smin(
                    min(sdTeeth(q),
                        opSmoothSubtraction(
                                            sdMouthCavity(q),
                                            min(
                                                smin(
                                                        smin(sdSphere(p, 0.57),sdBox(p, vec3(0.3)),0.2),
                                                        min(sdBox((p-vec3(0,-0.1,0))*rotx(0.88), vec3(0.25,0.1, 0.8)),
                                                            sdBox((p-vec3(0,0,-0.2))*rotx(0.3), vec3(0.5,0.0, 0.52))
                                                            ),
                                                        0.04
                                                    ),
                                                sdGoggles(p)
                                                ),
                                             0.02
                                            )
                        ),
                        sdNose(p), 
                        0.0
                        ),
                   sdBeard(p),
                   0.005
                 );
                } else 
                    return bound;
}

float sdLegs(vec3 p, float leftOrRight){
    p *= rotx(cos(iTime*WALKSPEED)*0.2*leftOrRight+0.5);
    return min(
                smin(
                    sdCapsule(p, vec3(0), vec3(0., -1., 0), 0.5),
                    sdBox((p-vec3(0., -1.4, .8)*rotx(.25*3.14))+vec3(0,sin((p.z-1.5)*2.)*0.1,0), vec3(0.3, -0.2, 0.5))-0.2,
                    0.1),
                .5);
    }

float sdPants(vec3 p){
    vec3 q = opSymX(p)-vec3(.8, -2., 0);
    vec3 knee = vec3(0.1, -0.7, 0);
    q *= rotx(sin(iTime*WALKSPEED)*-0.5*p.x/abs(p.x)-0.5);
   return  smin(
               smin(sdSphere(p-vec3(0,-2.,0), 0.5),
                    sdCapsule(
                                q, 
                                vec3(0), 
                                knee,
                                0.6), 
                    0.2),
                sdLegs(q-knee, p.x/abs(p.x)),
                0.1);
}

float sdGiant(vec3 p){
    p.y += abs(cos(iTime*WALKSPEED))*0.1;
    //Adding this bounding volume resulted in 5x fps and 0.25x compile time on my computer
    float bound = sdBox(p, vec3(4,4.5,3.5));
    if (bound < 1.){
        vec3 h = p*rotx(cos(iTime*WALKSPEED*2.)*0.1);
        p*=roty(sin(iTime*WALKSPEED)*-0.051);
        vec3 q = p*rotx(cos(iTime*WALKSPEED*2.)*0.1);

        return min(
                    min(
                        smin(
                            min(
                                smin(
                                     smin(
                                         sdBox(q-vec3(0,0.5,0)  ,vec3(1.,.2,0.3))-0.25,
                                         sdBox(q-vec3(0,-0.35,0),vec3(1.,0.7,0.15))-0.25,
                                         0.75
                                         ),
                                      sdPants(p),
                                      0.1
                                     ),
                                min(
                                    sdArm(q),
                                    sdPlate(q)
                                    )
                                ),
                             sdShoulder(q),
                             0.1
                            ),
                        sdHead(h)),
                        sdBelt(p)
                        );
    } else
        return bound;


}

float plaIntersect( in vec3 ro, in vec3 rd, in vec4 p )
{
    return -(dot(ro,p.xyz)+p.w)/dot(rd,p.xyz);
}

float sdPlane(vec3 p, float h){
    return p.y - h;
}

float sdGround(vec3 p){
    return sdPlane(p, -4.5);
}

float sdScene(vec3 p){
    return min(sdGiant(p), sdGround(p));
}

float pow2(float v){
    return v*v;
}

// IQ's normal function.
vec3 calcNormal(vec3 p){ 

    vec3 e = vec3(.001, 0, 0);

    vec3 n = vec3(0.0);
    for( int i = min(iFrame, 0); i<4; i++ )
    {
        vec3 e = .57735*(2.*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1)) - 1.);
        n += e*sdScene(p + .001*e);
    }

    return normalize(n);
}


float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0;
    for( float t=mint; t<maxt; )
    {
        float h = sdGiant(ro + rd*t);
        if( h<0.001 )
            return 0.0;
        res = min( res, k*h/t );
        t += h;
    }
    return 1.-pow4(1.-res);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
