            float beard = sdBeard(h);
            float nose = sdNose(h);
            float teeth = sdTeeth(h);
            float goggles = sdGoggles(h);
            if (beard < 0.04)
                col = mix(col, vec3(240, 74, 0) / 256., clamp(0.04/clamp(beard,10e-10,1.),0.,1.));
            else if (nose <0.02)
                col = mix(col, vec3(255,219,172)/350., clamp(0.02/clamp(nose,10e-10,1.),0.,1.));
            else if (teeth < 0.01)
