#define MAX_STEPS 120
#define MAX_DIST 120.
#define SURF_DIST .01

float GetDistance(vec3 point) {

    float texture = sin((iTime + point.x)*.5)*2.7+cos(point.z*2.5+sin(iTime)*5.)*.1+sin(point.x*5.+point.z*10.)*0.03;
    float planeDist = point.y + texture*.5;

    float planeDist2 = -point.y + 3. + texture*.5;;

    return min(planeDist,planeDist2);
}

vec3 GetNormal(vec3 point) {
  float distance = GetDistance(point);
  vec2 e = vec2(.01,0);

  vec3 normal = distance - vec3(
      GetDistance(point-e.xyy),
      GetDistance(point-e.yxy),
      GetDistance(point-e.yyx));

  return normalize(normal);
}

vec2 RayMarch(vec3 rayOrgin, vec3 rayDirection) {
	float distance=0.;

    int steps = MAX_STEPS; 
    int i = 0;

    for(i=0; i<steps; i++) {
    	vec3 point = rayOrgin + rayDirection * distance;
        float surfaceDistance = GetDistance(point);
        distance += surfaceDistance;
        // Stop marching if we go too far or we are close enough of surface
        if(distance>MAX_DIST || surfaceDistance<SURF_DIST) break;
    }

    return vec2(distance,i);
}

float GetLight(vec3 point, vec3 normal, vec3 lightPos) {    

  vec3 direction = normalize(lightPos-point);

  float dif = clamp(dot(normal, direction), 0., 1.);

  float d = RayMarch(point+normal*.1, direction).x;
  if ( d < length(lightPos-point)) dif *= .5;

  return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // put 0,0 in the center
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // camera   
    vec3 rayOrgin = vec3(0, 1.5, 0);
    vec3 rayDirection = normalize(vec3(uv.x, uv.y, 1));

    vec2 d = RayMarch(rayOrgin, rayDirection);

    vec3 col = vec3(0.);

    vec3 lightPos = vec3(1, 1.5, 1);

    if (d.x < 120.) {
        vec3 p = rayOrgin + rayDirection * d.x;
        vec3 n = GetNormal(p);
        float light = GetLight(p, n, lightPos);
        // color
        float dist = d.x/50.;
        col = vec3(
            light-dist,
            light-dist/1.5+(d.y*0.004),
            light-dist/2.+(d.y*0.005)
        );
    }
    fragColor = vec4(col,1.0);
}

// Modified version of font loader from https://www.shadertoy.com/view/ltcXzs
void drawChar(inout vec3 color, in vec3 charColor, in vec2 uv, in vec2 pos, in vec2 size, in int char) {
    uv = (uv - pos) / size + 0.5;
    vec2 charUv = uv / 16.0;
    vec2 dfdx = dFdx(charUv);
    vec2 dfdy = dFdy(charUv);
    if (all(lessThan(abs(uv - 0.5), vec2(0.5)))) {
        float val = textureGrad(iChannel0, charUv + fract(vec2(char, 15 - char / 16) / 16.0), dfdx, dfdy).r;
        color = mix(color, charColor, val);
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Default shader
    vec2 uv = fragCoord / iResolution.xy;
    vec3 color = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0.0, 2.0, 4.0));

    // Aspect correct, centered UVs and 2 pixel AA width for soft edges
    uv = fragCoord / iResolution.y - vec2(0.0, 0.5);
    float unit = 2.0 / iResolution.y;

    // Graph sound wave (MUCH slower than actual speed)
    float x = mod(0.02 * (uv.x + 2.0 * iTime), 11.0);
    float graphDist = abs(uv.y - 0.25 * makeNoise(x));
    float slope = 0.01 * (makeNoise(x + 0.0001) - makeNoise(x - 0.0001)) / 0.0002;
    graphDist /= sqrt(slope * slope + 1.0);
    graphDist += 0.0025;

    color = mix(color, vec3(0.0), smoothstep(unit, 0.0, graphDist));

    fragColor = vec4(color, 1.0);

    // Uncenter
    uv += vec2(0.0, 0.5);

    // "Sound In" notification
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.3, 0.3), vec2(0.1), 83); // S
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.4, 0.3), vec2(0.1), 79); // O
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.5, 0.3), vec2(0.1), 85); // U
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.6, 0.3), vec2(0.1), 78); // N
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.7, 0.3), vec2(0.1), 68); // D

    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.9, 0.3), vec2(0.1), 73); // I
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(1.0, 0.3), vec2(0.1), 78); // N
}

