// *********************************** PERLIN NOISE **********************************
// ***********************************************************************************
// ***********************************************************************************




vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}
vec4 taylorInvSqrt(vec4 r){return 1.79284291400159 - 0.85373472095314 * r;}
vec3 fade(vec3 t) {return t*t*t*(t*(t*6.0-15.0)+10.0);}
float cnoise(vec3 P){
  vec3 Pi0 = floor(P); // Integer part for indexing
  vec3 Pi1 = Pi0 + vec3(1.0); // Integer part + 1
  Pi0 = mod(Pi0, 289.0);
  Pi1 = mod(Pi1, 289.0);
  vec3 Pf0 = fract(P); // Fractional part for interpolation
  vec3 Pf1 = Pf0 - vec3(1.0); // Fractional part - 1.0
  vec4 ix = vec4(Pi0.x, Pi1.x, Pi0.x, Pi1.x);
  vec4 iy = vec4(Pi0.yy, Pi1.yy);
  vec4 iz0 = Pi0.zzzz;
  vec4 iz1 = Pi1.zzzz;

  vec4 ixy = permute(permute(ix) + iy);
  vec4 ixy0 = permute(ixy + iz0);
  vec4 ixy1 = permute(ixy + iz1);

  vec4 gx0 = ixy0 / 7.0;
  vec4 gy0 = fract(floor(gx0) / 7.0) - 0.5;
  gx0 = fract(gx0);
  vec4 gz0 = vec4(0.5) - abs(gx0) - abs(gy0);
  vec4 sz0 = step(gz0, vec4(0.0));
  gx0 -= sz0 * (step(0.0, gx0) - 0.5);
  gy0 -= sz0 * (step(0.0, gy0) - 0.5);

  vec4 gx1 = ixy1 / 7.0;
  vec4 gy1 = fract(floor(gx1) / 7.0) - 0.5;
  gx1 = fract(gx1);
  vec4 gz1 = vec4(0.5) - abs(gx1) - abs(gy1);
  vec4 sz1 = step(gz1, vec4(0.0));
  gx1 -= sz1 * (step(0.0, gx1) - 0.5);
  gy1 -= sz1 * (step(0.0, gy1) - 0.5);

  vec3 g000 = vec3(gx0.x,gy0.x,gz0.x);
  vec3 g100 = vec3(gx0.y,gy0.y,gz0.y);
  vec3 g010 = vec3(gx0.z,gy0.z,gz0.z);
  vec3 g110 = vec3(gx0.w,gy0.w,gz0.w);
  vec3 g001 = vec3(gx1.x,gy1.x,gz1.x);
  vec3 g101 = vec3(gx1.y,gy1.y,gz1.y);
  vec3 g011 = vec3(gx1.z,gy1.z,gz1.z);
  vec3 g111 = vec3(gx1.w,gy1.w,gz1.w);

  vec4 norm0 = taylorInvSqrt(vec4(dot(g000, g000), dot(g010, g010), dot(g100, g100), dot(g110, g110)));
  g000 *= norm0.x;
  g010 *= norm0.y;
  g100 *= norm0.z;
  g110 *= norm0.w;
  vec4 norm1 = taylorInvSqrt(vec4(dot(g001, g001), dot(g011, g011), dot(g101, g101), dot(g111, g111)));
  g001 *= norm1.x;
  g011 *= norm1.y;
  g101 *= norm1.z;
  g111 *= norm1.w;

  float n000 = dot(g000, Pf0);
  float n100 = dot(g100, vec3(Pf1.x, Pf0.yz));
  float n010 = dot(g010, vec3(Pf0.x, Pf1.y, Pf0.z));
  float n110 = dot(g110, vec3(Pf1.xy, Pf0.z));
  float n001 = dot(g001, vec3(Pf0.xy, Pf1.z));
  float n101 = dot(g101, vec3(Pf1.x, Pf0.y, Pf1.z));
  float n011 = dot(g011, vec3(Pf0.x, Pf1.yz));
  float n111 = dot(g111, Pf1);

  vec3 fade_xyz = fade(Pf0);
  vec4 n_z = mix(vec4(n000, n100, n010, n110), vec4(n001, n101, n011, n111), fade_xyz.z);
  vec2 n_yz = mix(n_z.xy, n_z.zw, fade_xyz.y);
  float n_xyz = mix(n_yz.x, n_yz.y, fade_xyz.x); 
  return 2.2 * n_xyz;
}


const float M_PI = 3.1415;
const float M_PI_DOUBLE = 6.2831;


vec2 applyRotation(vec2 base, float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return vec2(base.x*c-base.y*s, base.x*s+base.y*c);
}


vec2 bringVectorCloserTo(vec2 base, vec2 target, float value) {
    return target + vec2((base - target).x*value, (base - target).y*value);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    vec2 uv = fragCoord.xy/iResolution.y;
    uv = bringVectorCloserTo(uv, iResolution.xy/iResolution.y/2., .01/pow(distance(uv, iResolution.xy/iResolution.y/2.), 2.));
    uv = iResolution.xy/iResolution.y/2. + applyRotation(uv.xy - iResolution.xy/iResolution.y/2., distance(uv.xy, iResolution.xy/iResolution.y/2.)*2.+cnoise(vec3(distance(uv.xy, iResolution.xy/iResolution.y/2.)*noiseTwistingDistanceFactor+iTime*movementSpeedFactor, 0., iTime*noiseTwistingSpeedFactor))*noiseTwistingIntensity+iTime*twistingSpeedFactor);

    float opacity = 0.;

    float vignette;
    for (int i = 0; i < numSegments; i += 1) {
        if (atan(uv.y - 1./2., uv.x - iResolution.x/iResolution.y/2.)/M_PI*180. > float(i*(360/numSegments)-180) && atan(uv.y - 1./2., uv.x - iResolution.x/iResolution.y/2.)/M_PI*180. < float((i+1)*(360/numSegments)-180)) {
            vignette = (fragCoord.x/iResolution.x)*(fragCoord.y/iResolution.y)*(1.-fragCoord.x/iResolution.x)*(1.-fragCoord.y/iResolution.y)*15.;
            opacity = float(i)/float(numSegments-1)*vignette+random(fragCoord.xy/2.)*.2;
        }
    }

    vec4 color;
    color = vec4(opacity*( 1./pow(.5/distance(fragCoord.xy/iResolution.y, iResolution.xy/iResolution.y/2.), 1.2)  ) );

    fragColor = color;
}

#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float GetDist(vec3 p) {
    //float sd = sdBox(p, vec3(4.)) - 2.;

    vec2 uv = p.xz;

    // buggy + laggy:
    // float sc = 3.;
    // uv = floor(sc * uv)/sc + 0.5;

    uv *= Rot(0.1 * p.y);
    //uv.x = sabs(uv.x) - 1.5;

    float ext = 4.2 + 1.2 * cos(0.1 * p.y + iTime);
    uv = abs(uv) - ext;
    uv *= Rot(pi/4.);
    uv.x = abs(uv.x) - sqrt(ext) * sqrt(3./2.);

    uv *= Rot(p.y * 2.4 + iTime);

    float t = iTime;
    float r = 0.5;
    r *= 0.5 + 0.5 * cos(0.5 * t + 1.2 * p.y);       
    vec2 p0 = r * vec2(cos(t), sin(t));
    vec2 p1 = r * vec2(cos(t + pi), sin(t + pi));
    float d = sdSegment(uv * 1.35, p0, p1) - .8 - .5 * length(uv);

    // looks cool too:
     //d = sdSegment(uv * (1. + 0.1 * cos(3. * 3. * length(uv))), p0, p1) -0.055 - .5 * length(uv);  

    //d *= 0.8;
    return d;//-smin(d, -sd);
}

float RayMarch(vec3 ro, vec3 rd, float z) {

    float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 3, -20);
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    float IOR = 1.;
    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        vec3 pIn = p - 4. * SURF_DIST * n;
        vec3 rdIn = refract(rd, n, 1./IOR);
        float dIn = RayMarch(pIn, rdIn, -1.);

        vec3 pExit = pIn + dIn * rdIn;
        vec3 nExit = -GetNormal(pExit);

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        vec3 e = vec3(1);
        vec3 bcol = pal(dif * 0.35 + 0.2 + 0.2 * cos(0.1 * p.y + iTime), e, e, e, 0.5 * vec3(0,1,2)/3.);
       // col = 0.02 * clamp(col, 0., 1.);

        col = bcol;
        col *= 0.75 - 0.25 * n.y;

        // fresnel (interior + exterior)
        col += pow(1.+dot(rdIn, nExit), 5.);    
        col += pow(1.+dot(rd, n), 5.);
        col *= col * (1. + dot(rd, n));

        // cartoony outline effect (0.5 determines thickness, looks cool with smoothstep)
        col += bcol * step(0., 0.5 + dot(rd, n));
    }

    col = pow(col, vec3(.4545)) + 0.05;	// gamma correction

    fragColor = vec4(col,1.0);
}

