// Variant of Fabrice's https://www.shadertoy.com/view/sslBDj
// Tidied up so I could understand what was going on.
// Also colours, variable wave heights, better AA (takes into
// account slope of graph) and added some more movement.
void mainImage( out vec4 O, vec2 p ){
  float scale = 3.0;
  float H = 2.0; // Wave height
  float K = 6.0; // Band density
  if (iMouse.z > 0.0) {
    H = 6.0*iMouse.x/iResolution.x;
    K = 10.0*iMouse.y/iResolution.y;
  }
  p = scale*(2.0*p-iResolution.xy)/iResolution.y;
  //p *= 2.0; // Uncomment to see what is going on
  O = vec4(0);
  int N = int(1.2*K*(H+scale));
  float px = fwidth(p.y);
  float A = 1.0;
  float t = 0.618*iTime+p.x;
  float z = A*sin(t); // Vertical displacement
  for( int i = -N; i <= N; i++) {
    int ix = i;
    ix -= int(0.5*K*iTime);
    float offset = float(ix)/K;
    float a = p.x + sin(offset + iTime);
    float d = H*cos(a) - offset - p.y - 0.5*iTime + z;
    // Insert favourite approximate to sqrt here.
    float k = sqrt(1.0+sqr(H*sin(a)-A*cos(t)));
    //float k = 1.0+0.5*abs(H*sin(a)-A*cos(t)); // Not a bad one
    if (abs(d) > 3.0*k/K) continue;
    vec4 col = vec4(ix&1); // ix can be negative!
    col *= vec4(0.5+0.5*h2rgb(fhash(ix)),1);
    col *= 0.6 - 0.4*sin(a); // Shading
    O = mix(O,col,smoothstep(-px*k,px*k,d));
  }
  O = pow(O,vec4(0.4545));
}

