vec2 findCenterAndPurity(vec3 x)
{
    // Matrix derived for (c_smooth+s_smooth) to be an approximation of the macadam limit
    // this makes it some kind of g0-like estimate
    mat3 M = mat3(
        2.26775149, -1.43293879,  0.1651873,
        -0.98535505,  2.1260072, -0.14065215,
        -0.02501605, -0.26349465,  1.2885107);

    x = x*M;

    float x_min = min(x.r,min(x.g,x.b));
    float x_max = max(x.r,max(x.g,x.b));

    float c = 0.5*(x_max+x_min);
    float s = (x_max-x_min);

    // math trickery to create values close to c and s, but without producing hard edges
    vec3 y = (x-c)/s;
    float c_smooth = c + dot(y*y*y, vec3(1.0/3.0))*s;
    float s_smooth = sqrt(dot(x-c,x-c)/2.0);
    return vec2(c_smooth, s_smooth);
}


vec3 toLms(vec3 c)
{
    mat3 rgbToLms = mat3(
        0.4122214708, 0.5363325363, 0.0514459929,
        0.2119034982, 0.6806995451, 0.1073969566,
        0.0883024619, 0.2817188376, 0.6299787005);

    vec3 lms_ = c*rgbToLms;
    return sign(lms_)*pow(abs(lms_), vec3(1.0/3.0));
}

float softness_scale = 0.2;

vec3 toLms(float I, float hue, float sat)
{
    float a = cos(hue);
    float b = sin(hue);

    float l_ = + 0.3963377774f * a + 0.2158037573f * b;
    float m_ = - 0.1055613458f * a - 0.0638541728f * b;
    float s_ = - 0.0894841775f * a - 1.2914855480f * b;

    vec3 lms = vec3(l_,m_,s_);

    vec2 MC = findCenterAndPurity(lms);   
    lms -= MC.x;    
    lms *= sat;    
    lms += I;

    return lms;
}

vec2 approximateShapeA(float L, float hue, float sat)
{
    vec3 lms = toLms(L, hue, sat);

    float a = 1.9779984951f*lms.x - 2.4285922050f*lms.y + 0.4505937099f*lms.z;
    float b = 0.0259040371f*lms.x + 0.7827717662f*lms.y - 0.8086757660f*lms.z;

    float C = sqrt(a*a+b*b);
    a /= C;
    b /= C;

    float a2 = 2.0*a*b;
    float b2 = a*a - b*b;
    float a3 = 3.0*a - 4.0*a*a*a;
    float b3 = -3.0*b + 4.0*b*b*b;    

    // softness_scale 0.0
    // Estimated using https://colab.research.google.com/drive/1rYFDmHuv0MNzCHXQSwhBkJaMbTQzoXl3?usp=sharing
    return vec2(
        1.70021527e+00 + -8.21491826e-02*a +  1.73329721e-01*b +  1.73952306e-01*a2 +  4.95620904e-03*b2 + 1.94643985e-02*a3 +  2.39021796e-02*b3,
        3.07593575e-01 + 1.70513225e-02*a +   7.21549478e-03*b +  1.99728079e-02*a2 + -1.25118676e-03*b2 + -7.99372300e-04*a3 + 1.21489709e-03*b3
    );
}

vec2 approximateShapeB(float I, float hue, float sat)
{
    vec3 lms = toLms(I, hue, sat); 

    float a = 1.9779984951f*lms.x - 2.4285922050f*lms.y + 0.4505937099f*lms.z;
    float b = 0.0259040371f*lms.x + 0.7827717662f*lms.y - 0.8086757660f*lms.z;

    float C = sqrt(a*a+b*b);
    a /= C;
    b /= C;

    float a2 = 2.0*a*b;
    float b2 = a*a - b*b;
    float a3 = 3.0*a - 4.0*a*a*a;
    float b3 = -3.0*b + 4.0*b*b*b;    

    // softness_scale 0.2
    // Estimated using https://colab.research.google.com/drive/1rYFDmHuv0MNzCHXQSwhBkJaMbTQzoXl3?usp=sharing
    return vec2(
        1.45502420e+00 + -7.65623334e-02*a +  2.26234241e-01*b +  6.25596210e-02*a2 +  3.16995726e-02*b2 + 4.46107711e-02*a3 +  3.49876286e-02*b3,
        2.69987106e-01 + 1.69987106e-02*a +   1.00116716e-03*b +  1.02594717e-02*a2 + -6.08142494e-04*b2 + 2.29845394e-03*a3 + 2.13300284e-03*b3
    );
}


vec3 compute(float I, float hue, float sat)
{
    vec3 lms = toLms(I, hue, sat);

    lms = lms*lms*lms;

    float l = lms.x;
    float m = lms.y;
    float s = lms.z;

    vec3 rgbResult;
    rgbResult.r = + 4.0767245293f*l - 3.3072168827f*m + 0.2307590544f*s;
    rgbResult.g = - 1.2681437731f*l + 2.6093323231f*m - 0.3411344290f*s;
    rgbResult.b = - 0.0041119885f*l - 0.7034763098f*m + 1.7068625689f*s;

    return rgbResult;
}

vec3 softSaturate(vec3 x, vec3 a)
{
    a = 1.0+a;
    x = min(x, a);
    vec3 b = (a-1.0)*sqrt(a/(2.0-a));
    return 1.0 - (sqrt((x-a)*(x-a) + b*b) - b)/(sqrt(a*a+b*b)-b);
}

vec3 softClipColorOutsideGamutOnly(vec3 color)
{
    // soft clipping scaled to only have an effect if the color is outside the gamut

    float grey = 0.2;

    vec3 x = color-grey;

    vec3 xsgn = sign(x);
    vec3 xscale = 0.5 + xsgn*(0.5-grey);
    x /= xscale;

    vec3 absX = abs(x);

    float maxX = max(max(1.0,absX.r), max(absX.g, absX.b));

    float softness_scale = 0.5;
    float softness = softness_scale*(maxX-1.0);
    softness = softness/(1.0+softness);

    return grey + xscale*xsgn*softSaturate(absX, vec3(softness));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float h = (fragCoord.x / iResolution.x)* 3.141592 * 2.;
    float I = 2.0*fragCoord.y / iResolution.y;
    I = I - floor(I);
    float C = -0.5*log(0.5+0.5*sin(iTime));

    float C_max;

    for (int i=0; i<200; i++)
    {
        vec3 rgbTest = compute(I, h, 0.5*float(i)/200.0);
        float diff = length(rgbTest - clamp(rgbTest, 0., 1.));

        if(diff == 0.)
        {
            C_max = 0.5*float(i)/200.0;
        }
    }

    vec3 rgb;

    if (fragCoord.y / iResolution.y > 0.5)
    {
        vec2 ST = approximateShapeA(I, h, 1.0);

        float C_smooth = (1.0/((ST.x/I) + (ST.y/(1.0-I))));
        rgb = compute(I, h, min(C,C_smooth));
        rgb = softClipColorOutsideGamutOnly(rgb);

    }
    else
    {
        rgb = compute(I, h, min(C,C_max));
    }

    rgb = clamp(rgb,0.0,1.0);

    fragColor.rgb = Srgb3(vec3(rgb));
    fragColor.a = 1.;
}

