#define PI 3.14159
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 px = vec2(sin(iTime/5.),cos(iTime/10.))/2.+0.5;
    vec2 uv = fragCoord/iResolution.xy-px;

    vec2 pos = vec2((atan(uv.x,uv.y)+PI)/(PI),0.5/length(uv));

    vec4 tex0 = texture(iChannel0, pos+vec2(0.,iTime));
    // Output to screen
    fragColor = tex0*length(uv);
}

