float bounce(float dh,float a)
{
    if (dh==0.) return 0.;
    float g=10.;
    float v0 = sqrt(dh*4.*g);
    float t = mod(iTime + a*v0/g+ 0.*v0/g,v0/g); 
    return -g*t*t+v0*t;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;


    vec3 rd = vec3(uv,1.);
    rd = normalize(rd);
    vec3 o =vec3(sin(5.+5.*sin(iTime*5.))*.1,0.,iTime*4.);
    vec3 n=vec3(0.);
    float d = 0.;
    float e = 0.;
    float shadow = 1.;

    for(int i =0;i<99;i++)
    {
        vec3 p = o+d*rd;
        vec3 qq = p;
        p.xz = mod(p.xz,2.)-1.;
        float s = p.z-qq.z;

        float a =length(p+vec3(0.,-bounce(.3,s/5.),0.))-.5;
        float b = p.y+.5;
        if(a < b)
        {
            e = a;
            n =.5*(mod(iTime*5.+s,2.)+1.)*normalize(p);
        }
        else 
        {
            e = b;
            n = vec3(0.,1.,0.);

        }
        d +=e;
    }
    float col;
    vec3 sun = vec3(cos(iTime*7.),1.,sin(iTime*5.));
    sun=normalize(sun);


    if(n==vec3(0.,1.,0.))
    {
        vec3 p = o+d*rd;
        float d2=0.;
        float ee = 1.;
        for(int i =0;i<36;i++)
        {
            vec3 q = p+d2*sun;
            vec3 qq = q;

            q.xz = mod(q.xz,2.)-1.;
        float s = q.z-qq.z;
            ee = length(q+vec3(0.,-bounce(.3,s/5.),0.))-.5;
            d2+=ee;
        }
        if (ee<1e-2)
        {
            shadow = 0.;
        }
    }



    if(e<1e-4) 
    {
        col = shadow * max(0., dot(n,sun))/d;
    }
    else col = 0.;

    fragColor = vec4(vec3(col),1.0);
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}


