        //uv -= (t2.yx)*(.25+(t2.x+t2.y));

        t2 = triangle_wave(uv.yx-.5,scale);
        //t2 = triangle_wave(uv.yx-.5,scale)/(1.+t2);

        //t2 = triangle_wave(uv.yx-.5,scale)*(1.-t2/3.);


        uv.yx = (triangle_wave(uv,scale)-t2)/scale;
        col.x = abs(uv.y+uv.x-col.x);
        col = abs(col.yzx);
        //col.x = abs(uv.y+uv.x-col.x*2.);
        //col = abs(col.yzx-1.);
    }
    fragColor = vec4(col,1.0);   
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float m = 0.5 + 0.5 * thc(6., 0.5 * iTime);

    // change me
    float a1 = 1. + 4. * m;
    float a2 = 1.;// * uv.x;
    float sc = 0.01;// + 3.99 * m; // sc = 0.01 is basically cos 

    float th = mix(0.03, 0.005, 1.-m); //0.0025;

    float t = .5 * iTime;
    t += 4. * m * length(uv);
    float n = 50.;

    float d = 10.;
    float s = 0.;

    float myi = 0.;

    for(float i = 0.; i < n; i++) {
        float o = 1. * pi * i / n;

        float r = 0.45;// * (0.5 + 0.5 * thc(10., t + 0. * o));

        float c = cos(t + o), b = sin(t + o);
        vec2 p = r * vec2(thc(sc, t + a1 * o), ths(sc, t + a2 * o));
        vec2 q = r * vec2(thc(sc, t + a1 * b * o), ths(sc, t + a2 * c * o));
        float d2 = sdSegment(uv, p, q);
        if (d > d2) {
            d = d2;
            s = max(s, step(d, th));
            myi = i;
        }
    }

    //s = step(d, 0.025);

    vec3 col = vec3(s);

    vec3 e = vec3(1.);
    col *= pal(1.-myi / n, e, e, e, 0.45 * vec3(0,1,2)/3.);
    col = sqrt(col + 0.02);
    fragColor = vec4(col,1.0);
}

