{   float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}

vec2 RotarEsferas(vec3 p)
{
  vec3 p0=p;  
 float nesf=14.0;
 float ang=360.0/nesf;
 vec2 dfin=vec2(9999.9 ,-1.0);

 p0= rotate_y(p0-vec3(0.0,0.0,0.0), radians(-iTime*50.0));

 for (float i=0.0; i<nesf;i++)
    {   
        vec3 p3= rotate_y(p0, radians(ang*i));
        float d3= sdSphere(p3-vec3(15.0,0.0,0.0), 1.0 );
        dfin=opU2( dfin, vec2( d3,float(i)+1.0));

    }
    return dfin;
}

vec2 GetDist(vec3 p  ) 
{	vec2 res= vec2(9999.0, -1.0);  
    p.y=p.y-5.0;
    vec3 p0=p;
	float planeDist1 = p.y+5.0;  //piso inf

    res =opU2(res, vec2(planeDist1,57.0));
    vec2 re1= RotarEsferas(p );
    res =opU2(res, re1);

    p=p0;

    float d1=sdTorus( p, vec2(15.0,2.0) );
    float d2=sdTorus( p, vec2(15.0,1.8) );
    float d3=sdBox( p-vec3(0.0,1.0,-10.5), vec3(17.0,1.5,7.0) );
    d1= differenceSDF(d1, d2);
    d1= differenceSDF(d1, d3);

    res =opU2(res, vec2(d1,60.0));

    return res;
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

vec2 RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   float t = 0.1;
    float tmax = 40.0;
    vec3 p;
    vec2 hit, object=vec2(0.1,0.0);


   for(int i=0; i <= PMaxSteps; i++) 
    {  	p = ro + rd*object.x;
        hit = GetDist(p);

        object.x += hit.x;
        object.y = hit.y;
        if (abs(hit.x) < EPSILON || object.x > MAX_DIST) break;

    }
    return object;

}


float getSoftShadow(vec3 p, vec3 lightPos) {
    float res = 1.0;
    float dist = 0.01;
    float lightSize = 0.03;
    for (int i = 0; i < MAX_STEPS; i++) {
        float hit = GetDist(p + lightPos * dist).x;
        res = min(res, hit / (dist * lightSize));
        dist += hit;
        if (hit < 0.0001 || dist > 60.0) break;
    }
    return clamp(res, 0.0, 1.0);
}

float occlusion(vec3 pos, vec3 nor)
{   float sca = 2.0, occ = 0.0;
    for(int i = 0; i < 10; i++) {
        float hr = 0.01 + float(i) * 0.5 / 4.0;
        float dd = GetDist(nor * hr + pos).x;
        occ += (hr - dd)*sca;
        sca *= 0.6;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );    
}

vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro,vec3 color, float t) 
{   vec3 lightPos=lp;
    vec3 hit = ro + rd * t;
    vec3 norm = GetNormal(hit);
    vec3 light = lightPos - hit;
    float lightDist = max(length(light), .001);
    float atten = 1. / (1.0 + lightDist * 0.125 + lightDist * lightDist * .05);
    light /= lightDist;

    vec3 R = reflect(-light, norm);
    vec3 V = -rd;

    float shadow = getSoftShadow(hit, normalize(lightPos));
    // occ
    float occ = occlusion(hit, norm);

    vec3 ambient= 0.5*color*clamp(0.5 + 0.5*norm.y, 0.0, 1.0);
    vec3 fresnel =  color *  pow(clamp(1.0 + dot(rd, norm), 0.0, 1.0), 2.0);;

    float dif = clamp(dot(norm, light), 0.0, 1.0);
    dif = pow(dif, 4.) * 2.;
    vec3 diffuse =  color * dif;

    float specular = pow(max(dot(R, V), 0.), 8.);
     // back
    vec3 back = 0.5 * color * clamp(dot(norm, -light), 0.0, 1.0);
       vec3 colOut = occ*color*(ambient+diffuse*shadow+.35 +back) + vec3(.7,.9,1)*specular*specular;

    return colOut;
}

vec3 Getluz(vec3 p, vec3 ro, vec3 rd, vec3 nor , vec3 colobj ,vec3 plight_pos, float tdist)
{  float intensity=1.0;
     vec3 result;
    result = lightingv3( nor, p, plight_pos,  rd,ro, colobj, tdist);
    return result;
}

vec3 render_sky_color(vec3 rd)
{   float t = (rd.x + 1.0) / 2.0;
    vec3 col= vec3((1.0 - t) + t * 0.3, (1.0 - t) + t * 0.5, (1.0 - t) + t);
    vec3  sky = mix(vec3(.0, .1, .4)*col, vec3(.3, .6, .8), 1.0 - rd.y);
	return sky;
}

//https://www.shadertoy.com/view/4lcSRn   ///IQ
vec3 pattern( in vec2 uv )
{   vec3 col = vec3(0.4);
    col += 0.4*smoothstep(-0.01,0.02,cos(uv.x*0.5)*cos(uv.y*0.5)); 
    col *= smoothstep(-1.0,-0.98,cos(uv.x))*smoothstep(-1.0,-0.98,cos(uv.y));
    return col;
}


vec3 GetMaterial(vec3 p,  vec3 n, vec3 ro,  vec3 rd, int id_color)
{  	vec3 colobj; 
       if (id_color<50)
		{
            colobj=getColor(int( id_color)); 
            return colobj;
        }

    if (id_color==57)
        {return pattern( p.xz );}
    if (id_color==58)
        {return pattern( p.xy );}
    if (id_color==59)
        {return pattern( p.zy );}

     if (id_color==60)
   {   vec3 mat = vec3(0.8, 0.2, 0.2);	
		float f = smoothstep(0.3, 0.3001, mod(atan(p.x, p.z), 0.65));
        mat = mix(mat, vec3(0.7, 0.7, 0.8), 1.0 - f);
        return mat;
   }

}


vec3 linear2srgb(vec3 c) 
{ return mix(12.92 * c,1.055 * pow(c, vec3(1.0/1.8)) - 0.055, step(vec3(0.0031308), c)); }

vec3 exposureToneMapping(float exposure, vec3 hdrColor) 
{ return vec3(1.0) - exp(-hdrColor * exposure); }


vec3 ACESFilm(vec3 x)
{   float a,b,c,d,e;
    a = 2.51; b = 0.03; c = 2.43; 
    d = 0.59; e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

vec3 Render(vec3 ro, vec3 rd)
{  vec3 col = vec3(0);
  vec3 p;

     vec2 hit=RayMarch(ro,rd, MAX_STEPS);
      if(hit.x<MAX_DIST) 
       {   p = (ro + rd * hit.x );  
        vec3 nor=GetNormal(p);
        vec3 colobj;
        colobj=GetMaterial( p, nor, ro, rd,  int(hit.y));
        vec3 result;
         result=  Getluz( p,ro,rd, nor, colobj ,light_pos1,hit.x)*light_color1;
        result+= Getluz( p,ro,rd, nor, colobj ,light_pos2,hit.x)*light_color2;
        col= result/2.0;
        col= (ACESFilm(col)+linear2srgb(col)+col+ exposureToneMapping(3.0, col))/4.0 ;
    }
    else if(hit.x>MAX_DIST)
    col= render_sky_color(rd);
   return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float t;
    t=mod(iTime*1.0,360.0);


 	light_pos1= vec3(-10.0, 120.0, 25.); light_color1=vec3( 1.0,1.0,1.0 );
 	light_pos2= vec3(10.0, 20.0, -25.0 ); light_color2 =vec3( 1.0,1.0,1.0 ); 


   vec3 ro=vec3(0.0,10.0,-28.0);
   vec3 rd=normalize( vec3(uv.x,uv.y,1.0));
   rd= rotate_x(rd, radians(20.0));
    vec3 col= Render( ro,  rd);

    fragColor = vec4(col,1.0);
}

// Modified version of font loader from https://www.shadertoy.com/view/ltcXzs
void drawChar(inout vec3 color, in vec3 charColor, in vec2 uv, in vec2 pos, in vec2 size, in int char) {
    uv = (uv - pos) / size + 0.5;
    vec2 charUv = uv / 16.0;
    vec2 dfdx = dFdx(charUv);
    vec2 dfdy = dFdy(charUv);
    if (all(lessThan(abs(uv - 0.5), vec2(0.5)))) {
        float val = textureGrad(iChannel0, charUv + fract(vec2(char, 15 - char / 16) / 16.0), dfdx, dfdy).r;
        color = mix(color, charColor, val);
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Default shader
    vec2 uv = fragCoord / iResolution.xy;
    vec3 color = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0.0, 2.0, 4.0));

    // Aspect correct, centered UVs and 2 pixel AA width for soft edges
    uv = fragCoord / iResolution.y - vec2(0.0, 0.5);
    float unit = 2.0 / iResolution.y;

    // Graph sound wave (MUCH slower than actual speed)
    float x = mod(0.02 * (uv.x + 2.0 * iTime), 11.0);
    float graphDist = abs(uv.y - 0.5 * makeNoise(x));
    float slope = 0.02 * (makeNoise(x + 0.0001) - makeNoise(x - 0.0001)) / 0.0002;
    graphDist /= sqrt(slope * slope + 1.0);
    graphDist += 0.0025;

    color = mix(color, vec3(0.0), smoothstep(unit, 0.0, graphDist));

    fragColor = vec4(color, 1.0);

    // Uncenter
    uv += vec2(0.0, 0.5);

    // "Sound In" notification
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.3, 0.3), vec2(0.1), 83); // S
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.4, 0.3), vec2(0.1), 79); // O
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.5, 0.3), vec2(0.1), 85); // U
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.6, 0.3), vec2(0.1), 78); // N
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.7, 0.3), vec2(0.1), 68); // D

    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.9, 0.3), vec2(0.1), 73); // I
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(1.0, 0.3), vec2(0.1), 78); // N
}

