#define TAU 6.2831853072

// https://www.shadertoy.com/view/4djSRW
float Hash11(in float p) {
    p = fract(p * 0.1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

float snoise(in float x) {
    return mix(Hash11(floor(x)), Hash11(ceil(x)), smoothstep(0.0, 1.0, fract(x)));
}

float makeNoise(in float time) {
    float sound = 0.0;

    float t = 5.0 * time;
    //float n = max(2.0, 6.0 * abs(mod(0.05 * floor(t) * floor(t), 2.0) - 1.0));
    //float n = max(2.0, 6.0 * abs(mod(0.2 * pow(floor(t), 1.5), 2.0) - 1.0));
    //float n = floor(6.0 * snoise(0.25 * floor(t))) + 2.0;

    // Randomly hacked together
    float s = floor(t);
    float n = mod(s, 6.0) + mod(s, 2.0) + 2.0;
    n += mod(s, mix(2.0, 4.0, step(1.0, mod(time, 5.0))));
    n += floor(6.0 * snoise(0.25 * s));
    n = floor(0.5 * n) + 1.0;

    // Smooth transition to avoid clicks
    t = fract(t);
    float env = smoothstep(0.0, 0.08, min(t, 1.0 - t));

    // Random frequencies that sound good together
    // The gaussian weight filters out high frequencies
    for (float i=0.0; i < 50.0; i++) {
        float freq = floor(550.0 * floor(Hash11(i) * n) / n);
        sound += sin(TAU * fract(freq * time)) * env * exp(-0.00001 * freq * freq) / 25.0;
    }

    return sound;
}

