/*
Unfortunately, this shader does not work on many systems as intended 
since it is very sensitive on precision (see comments).
Please use the test flag below to see which numbers (int(iTime)) look 
good on your system. Compare with array va.
*/
//#define test

#define PI 3.14159265


vec4 flip(vec2 p, float pi) {
    vec3 a = vec3(0.0998); int ni = 58;  // parameters
    vec3 q= vec3(p, .06);  // scale
    vec3 t;
    for(int i=0; i<ni; i++) {
        a = sin(a);  // works by accident
        a = vec3(1.-a.y-a.x,a.y,a.x)*pi;

        vec3 c = cos(a), s = sin(a);
        mat3 N= mat3(1, 0, 0,
                 -c.x, s.x, 0,
                 -c.z, -(c.y+c.x*c.z)/s.x, 1);

        for(int j=0; j<3; j++) {
            t[j] = dot(q, N[j]);
            if(t[j] < 0.) {q -= 2.*t[j]*N[j]*vec3(1,1,0);}
        }
    }
	// q.xy: cartesian coords, t.xyz: triangle normal coords (t.x = q.x)
    return vec4(q.xy, t.yz);
}


#ifdef test

void mainImage(out vec4 O, vec2 U) {    
    U = (U - .5*iResolution.xy)/iResolution.y;        
    float pi = PI*(1. + floor(iTime)*1e-7);
    O = flip(U,pi);
    O = vec4(dot(O,vec4(0,50,-30,0)));
}

#else

void mainImage(out vec4 O, vec2 U) {

    int[] va = int[](92,-22,-33,-60,-66,16,24,33,45,48,49,55,56,63,75);  // 15 patterns
    int na = 15;
    float a = 5., b = 6.;
    float t = iTime - .9;
    float f = sin(-t/b*PI);
    t += f*f*2.5;

    U = (U - .5*iResolution.xy)/iResolution.y;

    float l = length(U);
    f = (log(l) - t + a)/b;    
    int n = int(1.-f);  // pattern index
    f = fract(f);
    float l2 = exp(f*b - a);
    f = smoothstep(.5,.4,abs(.5-f));  // for gentle transitions
    f = min(f,l/l2);
    U *= l2/l;

    float v = float(va[n%na]);
    float pi = PI*(1. + v*1e-7);  // slight bend

    O = flip(U,pi);  // -> flipped coords
    O = f*vec4(dot(O,vec4(0,50,-30,0)));  // weighted sum

}

