float dot2( in vec2 v ) { return dot(v,v); }
mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(vec2(c, -s), vec2(s, c));
}

// This operation is based on the technique of elongation 
// https://iquilezles.org/articles/distfunctions

// This does not create a true distance field, but does not distort the field too much.
// If the shape is centered on the origin the distortion works well.
// If the shape is concave however, this will not work well.
// Note that just like the elongation method it produces zero length gradient in the center region
// This can break boolean operations like intersection and subtraction.
vec3 inflate(vec3 p, float r) {
    float pl = length(p);
    vec3 n = p / pl;
    return p - n * clamp(pl, -r, r);
}

float sdOctahedron( vec3 p, float s)
{
  p = abs(p);
  float m = p.x+p.y+p.z-s;
  vec3 q;
       if( 3.0*p.x < m ) q = p.xyz;
  else if( 3.0*p.y < m ) q = p.yzx;
  else if( 3.0*p.z < m ) q = p.zxy;
  else return m*0.57735027;

  float k = clamp(0.5*(q.z-q.y+s),0.0,s); 
  return length(vec3(q.x,q.y-s+k,q.z-k)); 
}
float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

float map( in vec3 p ) {
    mat2 rot = Rot(iTime);
    const float scale = 0.6;

    float d = 1e10;


    {
        vec3 p2 = p + vec3(-1.0, 0, 0);      
        p2.xz = p2.xz * rot;

        p2 /= scale;

        float factor = (sin(iTime) + 0.6) * 0.3;

        p2 = inflate(p2, factor);

        d = min(sdOctahedron(p2, 0.5) * scale, d);
    }
    {
        vec3 p2 = p + vec3(1.0, 0, 0);      
        p2.xz = p2.xz * rot;

        p2 /= scale;

        float factor = (sin(iTime) + 0.6) * 0.3;

        p2 = inflate(p2, factor);


        float d1 = sdSphere(p + vec3(1.0, 0, 0), 0.47);
        float d2 = sdOctahedron(p2, 0.5) * scale;

        d = min(max(d1,-d2), d);
    }

    return d;
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.0005;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) + 
					  e.xxx*map( pos + e.xxx*eps ) );
}

#if HW_PERFORMANCE==0
#define AA 1
#else
#define AA 3
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // camera
	vec3 ro = vec3( 0.0, 0.2, -2.0 );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv =          ( cross(uu,ww));

    // render
    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

        // raymarch
        const float tmax = 5.0;
        float t = 0.0;
        for( int i=0; i<256; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }


        // shading/lighting	
        vec3 col = vec3(0.0);
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            float dif = clamp( dot(nor,vec3(0.57703)), 0.0, 1.0 );
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,1.0,0.0));
            col = vec3(0.2,0.3,0.4)*amb + vec3(0.8,0.7,0.5)*dif;
        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

const int FSAA = 2;
const float EPS = 0.01;
const float FOVH = 70.0;
const float D = 0.1;
const float L = 200.0;
const int MAX_ITER = 100;
const float ROAD_RADIUS = 4.;
const float SPEED = 2.;

const vec3 CYAN = vec3(0.4,.95,1);
const vec3 PINK = vec3(1,.2,.9);
const vec3 BLACK = vec3(0,0,0);

// From https://www.shadertoy.com/view/Msf3WH
vec2 hashv( vec2 p )
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}
float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hashv(i+0.0)), dot(b,hashv(i+o)), dot(c,hashv(i+1.0)));
    return dot( n, vec3(70.0) );
}

// Minimum of distance wit hcolor.
vec4 min_sdf(vec4 a, vec4 b) {
    return a.w < b.w ? a : b;
}

// From https://www.shadertoy.com/view/WttXWX
uint hash(uint x)
{
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return x;
}

