vec4 sdf_building( vec3 p, vec3 col, vec3 b, bool get_color)
{
  vec3 q = abs(p) - b;
  float sdf_ = length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
  if(get_color) {
      vec3 bdv = smoothstep(vec3(-.6), vec3(-.4), q);
      float bd = max(bdv.x*bdv.y, max(bdv.x*bdv.z, bdv.y*bdv.z));
      col = mix(col, CYAN, bd);
  }
  return vec4(col,sdf_);
}
float sdf_box( vec3 p, vec3 b)
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

// Color in xyz and sdf in w.
vec4 sdf(in vec3 pos, bool get_color) {
    vec4 sdf_ = vec4(0,0,0,1000);

    const float R_0_X = 32.;
    const float R_0_Z = 32.;
    const float R_0_D = 100.;
    const int GM = 2;
    const int GN = 2;
    const float w = R_0_X/float(GN);
    const float we = .325*w;
    vec3 col;
    if(get_color) {
        uint col_hash = hash(uint(.6*abs(pos.x)) + (uint(1.5*abs(pos.y))<<12) + (uint(.6*abs(pos.z))<<16) + (pos.x > 0. ? 13u : 0u));
        int col_idx = int(col_hash % 16u);
        col = col_idx == 0 ? CYAN : (col_idx == 1 ? PINK : BLACK);
    } else {
        col = BLACK;
    }
    const float heights[4] = float[](42., 34., 34., 32.);
    for(int i = 0; i < GM; i++) {
        for(int j = 0; j < GN; j++) {
            int idx = GN * i + j;
            float height = heights[idx];
            vec3 c = vec3(-R_0_X/2.+w*(0.5+float(j)), 0.0, -R_0_Z/2.+w*(0.5+float(i)));
            vec3 pos0 = pos - c;
            pos0.x += sign(pos.x) * 15.;
            pos0.x = pos0.x-R_0_X*(clamp(round(pos0.x/R_0_X),pos.x > 0. ? 1. : -8.,pos.x > 0. ? 8. : -1.));
            pos0.z += R_0_D;
            pos0.z = pos0.z-R_0_Z*clamp(round(pos0.z/R_0_Z),-16.,0.);
            sdf_ = min_sdf(sdf_, 
                sdf_building(pos0, col, vec3(we, height, we), get_color)
            );
        }
    }

    //sdf_ = min_sdf(sdf_, vec4(1,1,1,length(pos-vec3(0,1,-5))-1.));
    {
        vec3 pos1 = pos;
        pos1.x -= ROAD_RADIUS;
        pos1.x = mod(pos1.x+ROAD_RADIUS, 2.*ROAD_RADIUS)-ROAD_RADIUS;
        sdf_ = min_sdf(sdf_, vec4(CYAN, sdf_box(pos1, vec3(0.05,0.05,400.))));
    }
    {
        vec3 pos2 = pos;
        pos2.x = abs(pos2.x);
        pos2.z -= SPEED * iTime;
        pos2.z = mod(pos2.z+ROAD_RADIUS, 2.*ROAD_RADIUS)-ROAD_RADIUS;
        sdf_ = min_sdf(sdf_, vec4(CYAN, sdf_box(pos2 - vec3(ROAD_RADIUS+200.,0,0), vec3(200.,.05,.05))));
    }

    return sdf_;
}

float non_zero(float x) {
    return x + (x >= 0. ? 0.0001 : -0.0001);
}

// Ray marching engine.
void rayMarcher(in vec3 cameraPos, in vec3 lookDir, in vec2 screenDim, in vec2 uv,
                inout vec3 col, in bool mirror) {
    vec3 up = vec3(0, 1, 0);
    vec3 lookPerH = normalize(cross(lookDir, up));
    vec3 lookPerV = normalize(cross(-lookDir, lookPerH));
    vec3 screenCenter = cameraPos + lookDir;
    vec3 screenPos = screenCenter + 0.5 * screenDim.x * uv.x * lookPerH
                     + 0.5 * screenDim.y * uv.y * lookPerV;

    vec3 rayDir = normalize(screenPos - cameraPos);

    // Simple way of doing the reflections, inaccurate if lookDir is not parallel to the ground.
    float diffuse;
    if(mirror) {
        // Noise in function of where the ray intersects the reflective surface.
        float s = -cameraPos.y / non_zero(rayDir.y);
        vec3 surf_inter = cameraPos + s * rayDir;
        if(s < 0.) return;
        vec2 surf_uv = (surf_inter.xz - vec2(0, SPEED*iTime));
        float noise_ = noise(5.*surf_uv);
        diffuse = abs(surf_uv.x) < ROAD_RADIUS ? 0.1 : 0.2;

        float noise_coef = abs(surf_uv.x) < ROAD_RADIUS ? 0.05 : 0.2;
        //cameraPos.y = -cameraPos.y;
        cameraPos = surf_inter;
        rayDir.y = -rayDir.y+noise_coef*noise_;
    }

    float t = 0.0;
    vec4 dist;
    vec3 pos;
    int iter = 0;
    do {
        pos = cameraPos + t * rayDir;
        dist = sdf(pos, false);
        t += dist.w;
        iter++;
    } while(t < L && iter < MAX_ITER && dist.w > EPS);

    if(pos.y < 0.) return;

    if(dist.w <= EPS && (!mirror || pos.z > -200.)) {
        dist = sdf(pos, true);
        float v = 1.0 - t / L;
        col = dist.xyz;
    } else {
        float s = (40.-cameraPos.z) / rayDir.z;
        vec2 sky_uv = (cameraPos + s*rayDir).xy;
        float stars_noise = noise(sky_uv);
        float white_intensity = smoothstep(0.6, 1.0, stars_noise);
        col = mix(col, vec3(1,1,1), white_intensity);
    }

    if(mirror) {
        col = mix(col, vec3(.5,0,.7), diffuse);
    }
}

vec4 sampleColor(in vec2 sampleCoord )
{
    float aspectRatio = iResolution.x / iResolution.y;
    float screenWidth = 2.0 * D * atan(0.5 * FOVH);

    vec3 cameraPos = vec3(0, 2.5, 0);
    vec3 lookDir = vec3(0, 0., -D);
    vec2 screenDim = vec2(screenWidth, screenWidth / aspectRatio);

    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = 2.0 * sampleCoord / iResolution.xy - 1.0;
    //vec2 uvf = 2.0* (sampleCoord - iResolution.xy / 2.0) / iResolution.y;

    // TODO: anti aliasing
    vec3 col = BLACK;
    rayMarcher(cameraPos, lookDir, screenDim, uv, col, true);
    rayMarcher(cameraPos, lookDir, screenDim, uv, col, false);

    // Very useful visual debugging
    //float sdf_2d = sdf(12.*vec3(uvf.x, 0., uvf.y-4.));
    //if(sdf_2d < 0.) col = vec3(cos(60.*sdf_2d), 0, 0);
    //else col = vec3(0, 0, cos(60.*sdf_2d));

    // Output to screen
    return vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec4 colSum = vec4(0);
    for(int i = 0; i < FSAA; i++) {
        for(int j = 0; j < FSAA; j++) {
            colSum += sampleColor(fragCoord + vec2(float(i) / float(FSAA), float(j) / float(FSAA)));
        }
    }
    fragColor = colSum / colSum.w;
}

float dot2( in vec2 v ) { return dot(v,v); }
mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(vec2(c, -s), vec2(s, c));
}

float sdHeart( in vec2 p )
{
    p.x = abs(p.x);

    if( p.y+p.x>1.0 )
        return sqrt(dot2(p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
    return sqrt(min(dot2(p-vec2(0.00,1.00)),
                    dot2(p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}
float sdRoundedX( in vec2 p, in float w, in float r )
{
    p = abs(p);
    return length(p-min(p.x+p.y,w)*0.5) - r;
}

float map( in vec3 p )
{
    p.y += 0.9;

    const float scale = 1.5;
    p /= scale;

    float d2 = sdHeart(p.xy);
    vec2 p2d = vec2(d2, p.z) * Rot(iTime);

    // This may produce a true SDF, but it depends on the 
    // shapes involved
    float d = sdRoundedX(p2d, 0.1, 0.05);

    return d * scale;
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.0005;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) + 
					  e.xxx*map( pos + e.xxx*eps ) );
}

#if HW_PERFORMANCE==0
#define AA 1
#else
#define AA 3
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an = 0.7*iTime;
	vec3 ro = vec3( 2.0*cos(an), 0.2, 2.0*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv =          ( cross(uu,ww));

    // render
    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

        // raymarch
        const float tmax = 5.0;
        float t = 0.0;
        for( int i=0; i<256; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }


        // shading/lighting	
        vec3 col = vec3(0.0);
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            float dif = clamp( dot(nor,vec3(0.57703)), 0.0, 1.0 );
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,1.0,0.0));
            col = vec3(0.2,0.3,0.4)*amb + vec3(0.8,0.7,0.5)*dif;
        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

#define TMIN 0.1
#define TMAX 200.
#define RAYMARCH_TIME 128
#define PRECISION .001
#define AA 3

#define S(v,r) smoothstep( r, r+ 3./iResolution.y, v )
float logo(vec2 uv);

float sdSphere(vec3 p, vec3 o, float r){
    return length(p-o)-r;
}
float sdCapsule(vec3 p,vec3 a, vec3 b,float r){
    vec3 ab = b - a;
    vec3 ap = p - a;
    float t = dot(ab, ap)/dot(ab,ab);
    t = clamp(t,0.,1.);
    vec3 c = a +t*ab;
    return length(p-c)-r;
}
float sdTorus(vec3 p,vec3 o, float r1,float r2){
    p -= o;
    return length( vec2(length(vec2(p.x,p.z))-r1,p.y) ) - r2;
}
float sdBox(vec3 p, vec3 o, vec3 s){
    p-=o;
    return length(max(abs(p)-s,0.));
}
float sdCylinder(vec3 p, vec3 a, vec3 b, float r) {
	vec3 ab = b-a;
    vec3 ap = p-a;
    float t = dot(ab, ap) / dot(ab, ab);
    vec3 c = a + t*ab;

    float x = length(p-c)-r;
    float y = (abs(t-.5)-.5)*length(ab);
    float e = length(max(vec2(x, y), 0.));
    float i = min(max(x, y), 0.);

    return e+i;
}
float f(vec3 p){
    float ds = sdSphere(p,vec3(0,1,3),1.);//sdf sphere
    float dc = sdCapsule(p,vec3(1,.5,2),vec3(3.5,.5,3),.5);//sdf capsule
    float dt = sdTorus(p,vec3(-2,.5,4),1.,.5);//sdf torus
    float db = sdBox(p,vec3(1,1,5),vec3(1,2,1));//sdf box
    float dcy = sdCylinder(p,vec3(1.5,1.7,2),vec3(4,.5,5),.5);//sdf cylinder
    float dp = p.y;//sdf plane y=0
    float d = min(ds,dc);
    d = min(d,dt);
    d = min(d,dp);
    d = min(d,db);
    d = min(d,dcy);
    return d;
}
float rayMarch(in vec3 ro, in vec3 rd) {
    float t = TMIN;
    for(int i = 0; i < RAYMARCH_TIME ; i++) {
        vec3 p = ro + t * rd;
        float d = f(p);
        t += d;
        if(d < PRECISION || t > TMAX)
            break;
    }
    return t;
}
// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal(in vec3 p) {
    const float h = 0.0001;
    const vec2 k = vec2(1, -1);
    return normalize(k.xyy * f(p + k.xyy * h) +
        k.yyx * f(p + k.yyx * h) +
        k.yxy * f(p + k.yxy * h) +
        k.xxx * f(p + k.xxx * h));
}

vec3 render(vec2 uv){
    vec3 lightPos = vec3(5.*cos(iTime), 5., 5.*sin(iTime)+3.);//light
    vec3 ro = vec3(0,2,-4);//camera
    vec3 rd = normalize(vec3(uv,2));

    vec3 color = vec3(0);//background
    vec3 amb = vec3(.1);//ambient color
    vec3 diffusecol = vec3(1.,1.,1.);//diffuse color

    float t = rayMarch(ro,rd);//raymarching

    vec3 p = ro+t*rd;
    vec3 n = calcNormal(p);
    vec3 l = normalize(lightPos-p);//lightDir
    vec3 dif = clamp(dot(l,n),0.,1.)*diffusecol;//lambert

    float shadow = rayMarch(p,l);
    if(shadow<length(lightPos-p)) dif*=.1;

    color = amb + dif;

    return color;
}
vec2 getuv(vec2 coord){
    return (2.*coord-iResolution.xy)/iResolution.y;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec3 color = vec3(0.);
    for(int m = 0; m < AA; m++) {
        for(int n = 0; n < AA; n++) {
            vec2 offset = 2. * (vec2(float(m), float(n)) / float(AA) - .5);
            vec2 uv = getuv(fragCoord + offset);
            color += render(uv);
        }
    }
    color /= float(AA*AA);
    color = mix(color,vec3(1),logo(uv));
    fragColor = vec4(color,1.0);
}


float logo(vec2 uv){
float n = 0.;
n += S(.05,abs(length(uv-vec2(-1.23,0))-.12));
n *= 1.-S(-1.25,uv.x);
n += S(.17,abs(uv.y))*S(.05,abs(uv.x+1.29));
n += S(.05,abs(length(uv-vec2(-1.5,0))-.12));
n += S(.05,abs(length(uv-vec2(-0.9,0))-.12));
n += (1.+S(-.6,uv.x)-S(.05,uv.y))*S(.05,abs(length(uv-vec2(-0.6,.03))-.09));
n += (1.-S(-.6,uv.x)+S(-.05,uv.y))*S(.05,abs(length(uv-vec2(-0.6,-.04))-.09));
return n;
}

vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
