    vec2 t2 = vec2(0.);
    vec2 t3 = vec2(0.);
        for(int k = 0; k < 12; k++){

            uv -= (t2.yx)/(scale);

            t2 = triangle_wave(uv.yx,scale);

            t3 = triangle_wave(uv-iTime/10.,scale);

            uv.yx = (t2+t3)/scale;

        col.x = abs(uv.y-uv.x+col.x);
        col = col.yzx;
        uv *= scale;

        }
    fragColor = vec4(col,1.0);   
}

