#define USE_TEX_NOISE_DERIVATIVE
// Uses a texture to store derivative noise data.
// Needs only one texture look-up.
// We can't set buffer sizes, so the speed up is good, but not great.
// because there's still an enormous amount of texture cache thrashing during the ray march.
// It really needs a 256x256 buffer.

// @Shadertoy - Please enable selectable buffer sizes! (That would be 💓)


#define THRESHOLD .4
#define FAR 12000.0
#define SCALE 2.8
#define MINRAD2 .25
const float minRad2 = clamp(MINRAD2, 1.0e-9, 1.0);
const float absScalem1 = abs(SCALE - 1.0);
const float AbsScaleRaisedTo1mIters = pow(abs(SCALE), float(1-10));
const vec3 surfaceColour1 = vec3(.8, .0, 0.);
const vec3 surfaceColour2 = vec3(.6, .6, 0.6);
const vec3 surfaceColour3 = vec3(.7, .7, 0.00);
const vec4 scale =vec4(SCALE, SCALE, SCALE, abs(SCALE)) / minRad2;

const vec3 sunLight  = normalize( vec3(  1.1, 0.8,  -0.8 ) );
const vec3 sunColour = vec3(1.0, .8, .6);
const vec3 cloudColour = vec3(.52, .35, .3);

vec3 cameraPos;
float gTime = 0.0;

//#define MOUSE_SCRUB

//-------------------------------------------------------------------------------------------------------

#ifdef USE_TEX_NOISE_DERIVATIVE

// Uses one texture LUT...
vec3 noiseD(in vec2 x) 
{
    vec2 f = fract(x);
    vec2 u = f*f*f*(f*(f*6.0-15.0)+10.0);
    vec2 du = 30.*f*f*(f*(f-2.0)+1.);
//    vec2 u = f*f*(3.0-2.0*f);
//    vec2 du = 6.0*f*(1.0-f);


    ivec2 p = ivec2(floor(x));
	vec4 n = texelFetch(iChannel0, p & TWRAP, 0);

	return vec3(n.x + n.y * u.x + n.z * u.y + n.w * u.x*u.y,
				du * (n.yz + n.w*u.yx));
}

#else

// iq's original code from 'elevated'...
// Thanks Inigo!
vec3 noiseD(in vec2 x )
{
    vec2 f = fract(x);

    vec2 u = f*f*f*(f*(f*6.0-15.0)+10.0);
    vec2 du = 30.*f*f*(f*(f-2.0)+1.0);
//    vec2 u = f*f*(3.0-2.0*f);
//    vec2 du = 6.0*f*(1.0-f);

    ivec2 p = ivec2(floor(x));
    float a = texelFetch(iChannel0, p&TWRAP, 0 ).x;
	float b = texelFetch(iChannel0, (p+ivec2(1,0))&TWRAP, 0 ).x;
	float c = texelFetch(iChannel0, (p+ivec2(0,1))&TWRAP, 0 ).x;
   	float d = texelFetch(iChannel0, (p+ivec2(1,1))&TWRAP, 0 ).x;

	return vec3(a + (b-a) * u.x+(c-a) *u.y+(a-b-c+d)*u.x*u.y,
				du*(vec2(b-a,c-a)+(a-b-c+d)*u.yx));
}

#endif

//-------------------------------------------------------------------------------------------------------
// Basic 3D noise using texture channel...
float noise( in vec3 p )
{
    vec3 f = fract(p);
    p = floor(p);
	f = f*f*(3.0-2.0*f);

	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 rg = textureLod( iChannel3, (uv+ 0.5)/256.0, 0.0).yx;
	return mix( rg.x, rg.y, f.z );
}

//-------------------------------------------------------------------------------------------------------

#define ANG2 1.33
#define ANG3 1.4
const mat2 rotMat = mat2(cos(ANG2), sin(ANG3), -sin(ANG3), cos(ANG2)) * 2.;

#define REM_SCALE 0.0035
#define RIM_POSITION  vec3(-1500.,-220.,-1500)

//-------------------------------------------------------------------------------------------------------
float terrain( in vec2 p, float z)
{

    float disObj = length(p+RIM_POSITION.xz)*.00044;
	float sc = clamp(disObj, 0.0,1.0);

    float a = 0.0;

    p+= vec2(200,222);

	p = p*0.0004;

    vec2  d = vec2(0.0);
    float b = 800.0 * sc;



    // Decrease iteration detail with distance with forced version...
    int iter;
    if (z >= 0.)
        iter = 16-int(log2(z*.2+.05));
    else
        iter = -int(z);

    iter = clamp(iter, 1, 16);

	for (int i = 0; i < iter; i++)
	{
       vec3 n = noiseD(p);

        d += n.yz;
        a += b*n.x/(1.+dot(d,d));
		b *= 0.483;
        p = rotMat*p;
        p -= n.yz*.3;
	} 



	return a;
}

mat3 rot3D; // ...Set in main
//-------------------------------------------------------------------------------------------------------
// Do the Mandelbox....
float mapRemnant(vec3 pos) 
{

    pos = pos + RIM_POSITION;
    pos = rot3D * pos;
    vec4 p = vec4(pos*REM_SCALE,1);
	vec4 p0 = p;

    // Shape always needs 8 iterations...
	for (int i = 0; i < 8; i++)
	{
		p.xyz = clamp(p.xyz, -1.0, 1.0) * 2.0 - p.xyz;

		float r2 = dot(p.xyz, p.xyz);
		p *= clamp(max(minRad2/r2, minRad2), 0.0, 1.0);

		p = p*scale + p0;
	}
	float l = ((length(p.xyz) - absScalem1) / p.w - AbsScaleRaisedTo1mIters) / REM_SCALE;

    return l;
}

vec3 remnantColour(vec3 pos) 
{
    pos = pos + RIM_POSITION;
    pos = rot3D * pos;
    vec4 p = vec4(pos*REM_SCALE,1);
	vec4 p0 = p;

	float trap = 1.0;

    // Just 6 iterations for colour selection...
	for (int i = 0; i < 6; i++)
	{

		p.xyz = clamp(p.xyz, -1.0, 1.0) * 2.0 - p.xyz;

		float r2 = dot(p.xyz, p.xyz);
		p *= clamp(max(minRad2/r2, minRad2), 0.0, 1.0);

		p = p*scale + p0;
		trap = min(trap, r2);
	}
	// |c.x|: log final distance (fractional iteration count)
	// |c.y|: spherical orbit trap at (0,0,0)
	vec2 c = clamp(vec2( 0.00015*length(p)-1., sqrt(trap) ), 0.0, 1.0);

    float t = mod(length(pos * REM_SCALE) - gTime*31.5, 16.0);
    vec3 surf = mix( surfaceColour1, vec3(.1, 2., 5.), smoothstep(0.0, .3, t) * smoothstep(0.6, .3, t));
	return mix(mix(surf, surfaceColour2, c.y), surfaceColour3, c.x);
}


//-------------------------------------------------------------------------------------------------------
// A big background sky drop... without sun or clouds for fogging.
vec3 getSky(in vec3 rd)
{
	float sunAmount = max( dot( rd, sunLight), 0.0 );
	float v = pow(1.0-max(rd.y,0.0),4.);
	vec3  sky = mix(vec3(.0, .06, .2), cloudColour, v);
	sky = sky + sunColour * pow(sunAmount, 4.0) * .2;
	return clamp(sky, 0.0, 1.0);
}

//-------------------------------------------------------------------------------------------------------
float findClouds2D(in vec2 p)
{
	float a = 1.0, r = 0.0;

    p*= .00008;
    p += 3.5;
    for (int i = 0; i < 6; i++)
    {
        r+= noise2D(p).x*a;
        a*=.51;
        p = rotMat *p;
    }
	return max(r-1., 0.0);
}

//-------------------------------------------------------------------------------------------------------
//..used by...
vec4 getClouds(vec3 pos, vec3 dir)
{
    if (dir.y < 0.0) return vec4(0.0);
    float d = (8000. / dir.y);
    vec2 p = pos.xz+dir.xz*d;
    float r = findClouds2D(p);
    float t = findClouds2D(p+normalize(sunLight.xz)*200.);    
    t = (max((r-t)*40., .3))+.8;
    vec3 col = cloudColour *t;
    float f = pow(max(dot(sunLight, dir), 0.0), 8.0)*5.;
    col= mix(col, sunColour*vec3(2., .9, .4), clamp(f, 0.0, 1.0));
    // returns colour and alpha...
    return vec4(col, r*.3);
}

//-------------------------------------------------------------------------------------------------------
vec3 addSun(in vec3 rd)
{
    float sunAmount = max( dot( rd, sunLight), 0.0 );
    return sunColour * pow(sunAmount, 300.0)*2.;
}

//-------------------------------------------------------------------------------------------------------
// Merge ground into the sky background for correct fog colouring...
vec3 applyFog( in vec3  col, in vec3 sky, in float dis,in vec3 pos, in vec3 dir)
{

    vec3 v = exp2(-dis*0.00006*vec3(1.,1.4,4)); 
    return mix(sky, col, v);

}


//-------------------------------------------------------------------------------------------------------
// Map the whole scene with two objects...
float map(vec3 p, float z)
{
    return  min(p.y-terrain(p.xz, z), mapRemnant(p));
}

//-------------------------------------------------------------------------------------------------------

// March the whole scene...
float rayMarch(in vec3 rO, in vec3 rD, in float t)
{
	float d = 0.;
    float adj = .4;// Estimate error adjust starts very small

    for(int j = min(0, iFrame); j < 300 && t < FAR; j++)
	{
        d = map(rO + t*rD, t);
        if (d < THRESHOLD) break;
        t += d * adj;
        // Instead of adding an arbitrary value to the estimation to speed up marching,
        // scale the value instead.
        adj += .004; // ... Works like magic here!
	}

	return t;
}

//-------------------------------------------------------------------------------------------------------
vec3 cameraPath( float t )
{
    vec2 p = vec2(2500.0 * sin(2.63*t), 2500.0 * cos(1.715*t) );
    p += 600.;
	return vec3(p.x,   terrain(p, -4.)+200.0+sin(gTime*3.+2.0)*100.0, p.y);
} 


//-------------------------------------------------------------------------------------------------------
vec3 getNormal(vec3 p, float dis)
{
    dis = dis*1.5/iResolution.y;
	vec2 e = vec2(0,clamp(dis, .1, 144.));
	return normalize(map(p, -15.0)-vec3(map(p - e.yxx, -15.), map(p - e.xyx, -15.), map(p - e.xxy, -15.)));
}

//------------------------------------------------------------------------------
float shadow( in vec3 ro, in vec3 rd, in float dis)
{
	float res = 1.0;
    float t = 1.0;
	float h;

    // Thankfully only a few steps are needed...
    for (int i = min(0, iFrame); i < 35; i++)
	{
        vec3 p =  ro + rd*t;

		h = map(p, dis);
		res = min(.6*h / t*t, res);
		t += h+6.0;
	}
    return clamp(res, .2, 1.0);
}

//-------------------------------------------------------------------------------------------------------
vec4 getDiffuse(vec3 pos, vec3 dir,  vec3 nor, float dis)
{
    vec4 dif = vec4(0);
    if ((pos.y-terrain(pos.xz, dis)) < THRESHOLD)
    {
        // Simple Terrain colouring...
        float n = cos(pos.z*.001+pos.x*.004+.8+pos.y*.001)*.4+.4;
        float dark = smoothstep(8000.0,3000.0,length(pos.xz+RIM_POSITION.xz));


        dif.xyz = vec3(mix(vec3(1.,.6,.4), vec3(.6, .6, .6), n));


        float s = max(0.,nor.y*nor.y);

        dif.xyz = mix(dif.xyz, vec3(s*.4, s*.1,0.), clamp(nor.x+nor.z+.4, 0.0, 1.));
        dif.w += .4;
        dif *= dark;



     }else
     {
         // Remnant colour...
         dif.xyz = remnantColour(pos)*2.; //... Wasted processing faffing around!
         dif.w = 1.5;
     }
    return dif;
}

//-------------------------------------------------------------------------------------------------------
// Calculate lighting from the sun...
vec3  doLighting(in vec4 dif, in vec3 pos, in vec3 nor, in vec3 eyeDir, in float dis)
{
	float h = dot(sunLight,nor);
	vec3 mat = dif.xyz * sunColour*(max(h, 0.0));
    vec3 ref = reflect(eyeDir, nor);
    mat += sunColour * pow(max(dot(ref, sunLight), 0.0), 80.0) * dif.w;

    mat *= shadow(pos+nor*2., sunLight, dis);
    // Some fake sky lighting...
    mat += vec3(.7,.6,.5) * max(0.0, -nor.y) *.05;

    return min(mat, 1.0);
}

//-------------------------------------------------------------------------------------------------------

vec3 cw, cu, cv;
vec3 getCamera(vec2 uv)
{
    vec3 cameraTar;
	cameraPos = cameraPath(gTime + 0.0);
	cameraTar = cameraPath(gTime + .25);
    cameraTar.y = cameraPos.y +sin(gTime*8.+2.5)*200.;

    // Camera lean on corners...
    // not working
    //vec3 far	 	= cameraPath(gTime + .2);
    //vec2 v1 = normalize(far.xz-cameraTar.xz);
    //vec2 v2 = normalize(cameraTar.xz-cameraPos.xz);
    //float roll = clamp(atan(cross2(v1,v2), dot(v1,v2))*.6, -.8, .8);

    // Boat-roll it instead...
	float roll = .4*sin(gTime*10.);

	vec3 cp = vec3(sin(roll), cos(roll),0.0);
    cw = normalize(cameraTar-cameraPos);
	cu = normalize(cross(cw,cp));
	cv = normalize(cross(cu,cw));
    return normalize(uv.x*cu + uv.y*cv + (length(uv*0.3)+.6)*cw);;
}

//-------------------------------------------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	float m = 0.0;
    #ifdef MOUSE_SCRUB
    m = (iMouse.x/iResolution.x)*50.0;
    #endif
	gTime = (iTime*.5+m+234.)*.1;
    vec2 xy = fragCoord.xy / iResolution.xy;
	vec2 uv = (-1.0 + 2.0 * xy) * vec2(iResolution.x/iResolution.y,1.0);

    rot3D = rotY(0.785398) * rotX(0.885398);

    if (abs(xy.y -.5) > .37)
	{
		// Top and bottom cine-crop. Don't discard; - it won't always be black.
		fragColor = vec4(0.0);
		return;
	}

    vec3 dir = getCamera(uv);

	vec3 col;
	float dist;

    float st = hash12(uvec2(fragCoord*iTime))*80.;
	dist = rayMarch(cameraPos, dir, st);

    vec3 sky = getSky(dir);

	if (dist >= FAR)
    {
		// Completely missed the scene...
		col = sky;

        col = min(vec3(1.0), col+addSun(dir));
        vec4 cc = getClouds(cameraPos, dir);

        col = mix(col, cc.xyz, cc.w);
	}
	else
	{
        // Render the objects...
        vec3 pos = cameraPos + dist * dir;
        vec3 nor = getNormal(pos, dist);
        vec4 dif = getDiffuse(pos, dir, nor, dist);
        col = doLighting(dif, pos, nor, dir, dist);
        col = applyFog(col, sky, dist, pos, dir);
	}


    // Post screen effects...
    // Sun flare stuff...
	float bri = dot(cw, sunLight)*.75;
	if (bri > 0.0)
	{
		vec2 sunPos = vec2( dot( sunLight, cu ), dot( sunLight, cv ) );
		vec2 uvT = uv-sunPos;
		uvT = uvT*(length(uvT));
		bri = pow(bri, 6.0)*.8;

		// glare = the red shifted blob...
		float glare1 = max(dot(normalize(vec3(dir.x, dir.y+.3, dir.z)),sunLight),0.0)*1.4;
		// glare2 is the cyan ring...
		float glare2 = max(1.0-length(uvT+sunPos*.5)*4.0, 0.0);
		uvT = mix (uvT, uv, -2.3);
		// glare3 is a yellow ring...
		float glare3 = max(1.0-length(uvT+sunPos*5.0)*1.2, 0.0);

		col += bri * vec3(1.0, .0, .0)  * pow(glare1, 12.5)*.1;
		col += bri * vec3(.2, 1.0, 1.) * pow(glare2, 2.0)*4.;
		col += bri *vec3(0.5, .5,0.0) * pow(glare3, 2.0)*5.;
	}

    // Got fed up of bloody smoothstepping everything 🙄
    // Use exponential curve instead (see in Common)
    col =  clamp(col, 0.0, 1.0);
    col *= fader(0.0, 4.0, iTime); 

    // Fade out to 4 mins for video rendering.
    //col *= fader(240.0, 230.0, iTime);

    // Contrast...
    col = col*.4 + (col*col*(3.0-2.0*col))*.6;
    // Gamma...
    col = sqrt(col);

    xy.y = xy.y*1.37 -.185; // ...for letterbox crop
	col *= 0.4 + .6*pow( 40.0*xy.x*xy.y*(1.0-xy.x)*(1.0-xy.y), .5);  // ...Vignette.
	fragColor=vec4(col, 1.0);
}

