vec2 mainSound( in int samp, float time )
{
    // Engine noise...
    vec2 noi = (noise2D(vec2(time*220.0))-.5)*.5 * noise2D(vec2(time*.3)+.1);
    noi += (noise2D(vec2(time*600.0))-.5)*.4 * noise2D(vec2(time*.2)+.1);
    noi += (noise2D(vec2(time*88.0))-.5)*.9 *  noise2D(vec2(time*2.)+.2);
    noi *= fader(.0, 3.0,time) * fader(180.0, 170.0,time);
    return noi;
}

