    i = vec2(1);

    float t = .3 * length(u) + iTime, 
    f = floor(t),
    a = t-f;
    f = 2.9325 * (f + pow(a*a*(3.-2.*a), 3.)); 

    t = .2;

    for(int j = 0; j < 5; j++) {   
        vec2 s = step(u, vec2(0)) - .5;

        a = f * s.x * s.y;
        u *= mat2(cos(a), -sin(a), sin(a), cos(a));

        i = .5 * i + s + .5;

        u = abs(u) - t;
        t *= .5;// + .25 * tanh(4. * cos(12. * u.x - iTime));
    }

    g = vec4(sqrt( 1. + cos(6.284 * (.13 * vec3(0,1,2) - min(-i.x, i.y))) ), 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
