    vec3 col = texture(iChannel0, uv).rgb;

    col = col*col*(3.0 - 2.0*col);

    col += 0.01*step(0.3, texture(iChannel1, uv*2.0).r * (1.0 - vign));

    col = 1.0 - exp(-0.5*col);

    col = pow(col, vec3(1.0/2.2));

    col *= 0.3 + 0.7*vign;
    fragColor = vec4(col, 1.0);
}

