#define S(v,r)    smoothstep( r, r+ 5./iResolution.y, v )
#define S1(v,r)   1.-S(v,r)
#define PI        3.1415926
#define AA        5.
#define wid       0.1

float func(in float x) {
    float T = 4. + 2. * sin(iTime);
    return sin(2. * PI / T * x);
    //return sin(x);
}
vec2 getuv(vec2 fragcoord){
    return (2.*fragcoord-iResolution.xy)/iResolution.y*4.;
}
float AAfunc(vec2 coord){
    float c = 0.;
    for(float m =0.;m<AA;m++){
        for(float n=0.;n<AA;n++){
            vec2 o = getuv(coord + (vec2(m,n)-.5*AA) / AA*2.);
            c += smoothstep(func(o.x)-wid,func(o.x)+wid,o.y);//
        }
    }
    c = abs(c/(AA*AA)-0.5)*2.;//remap to 0-1:more closed to 0 more closed to the funcline
    return smoothstep(300./iResolution.y,0.,c);
}

vec3 grid(vec2 uv){
    vec2 pos = floor(uv*2.);
    vec3 c = vec3(0.1)+mod(pos.x+pos.y,2.0)*0.1;
    c = mix(c,vec3(.3,.8,1),S1(abs(uv.x),0.01));
    c = mix(c,vec3(1,0,.3),S1(abs(uv.y),0.01));
    return c;
}
float logo(vec2 uv);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
