float logo(vec2 uv){
float n = 0.;
n += S(.05,abs(length(uv-vec2(-1.23,0))-.12));
n *= 1.-S(-1.25,uv.x);
n += S(.17,abs(uv.y))*S(.05,abs(uv.x+1.29));
n += S(.05,abs(length(uv-vec2(-1.5,0))-.12));
n += S(.05,abs(length(uv-vec2(-0.9,0))-.12));
n += (1.+S(-.6,uv.x)-S(.05,uv.y))*S(.05,abs(length(uv-vec2(-0.6,.03))-.09));
n += (1.-S(-.6,uv.x)+S(-.05,uv.y))*S(.05,abs(length(uv-vec2(-0.6,-.04))-.09));
return n;
}

/*

BUFFER A <= For information on the particle simulation & animation
IMAGE <= For information on the coloring and lighting

IMAGE
******************************************************

MAIN FEATURES:

1. A Hemispherical Normal function to calculate a 3d normal for 
every point on a 2d disk. This is explained in depth.

2. Skipping particles that are outside a certain distance from a given pixel 
since it wont factor into the color of that pixel

3. Lot's and lot's of comments!

*/

//noise function used for the truchet texture.
float noise(vec2 st){
    return fract(sin(dot(vec2(12.23,74.343),st))*43254.);  
}

//circle function for drawing parts of the disk
float circle(vec2 p, float r, float sf)
{    
    return smoothstep(r+EPS*sf, r-EPS*sf, length(p));
}

//a sin function to create a ripple effect on the disks
float SIN(vec2 p, float r)
{
    float c = length(p);
    return abs(sin(c*50./r))*(smoothstep(r+EPS, r-EPS,c));
}

/* Next, I create a 3d spherical normal out of a point on a disk
using trigonometry
imagine you are standing in a field but you are covered by this
dome. A complete half sphere dome. You are on this field inside 
and at any position in there, you are under a part of the "roof"
of the dome. The height to the roof where you are standing, is the 
z axis. Your position is x and y.
We know the dome will have a middle (where x and y = vec2(0,0)) the 
origin of the dome on the field. So we can find the length to the position
on the field where you are standing with length(xy).
Ok, so then we have a length, but we never actually found Z did we?
But we do have the length and so we can use trigonometry if we
let length be A and let Z be B.
So we have A but we don't have B or C.
Well if the dome is a "unit sphere" then we DO have C because it
will be the hypotenuse of length = 1.
Then B = sqrt(1.-A^2)
So we can return the normal as 
vec3(x from field ,y from field ,B from triangle)
*/
vec3 hemiNormal(vec2 center, vec2 point){
//we scale base to be 1 based on the radius scaled to only the 
//part of the circle we want to add highlights to. instead of the full
//radius, we want 0.6 of it.
    float base = length(point)/(RADIUS*0.6);
    float height = sqrt(1.-base*base);
    return vec3(point, height);
}

//This function puts a lot of things together
vec3 drawFancyCircle(vec3 col, 
                     vec2 uv,
                     int ball_id,
                     vec2 fragCoord, 
                     vec2 pos, 
                     float r){

    //I get the animation data for the given disk       
    vec4 anim_data = load0(vec2(float(ball_id)+0.5,1.5));
    //animation is the animation run time from 0 to 1
    float animation = anim_data.x;
    //This light is for the rim lighting of the disks
    vec2 light = vec2(1.,0.);
    //I move it around the screen
    light*=rot(iTime);

    //metal texture for the disks
    vec3 tx1 = vec3(texture(iChannel2,(pos)/r/2.)).rgb+0.;

    //super hacky texture call that allows
    //me to shift the texture by the circle
    //position, scale it by the texture radius,
    //adjust aspect ratio, and ajust the center again
    float scale = 0.6/r;
    vec3 tx2 = vec3(texture(iChannel3, 
    ((pos)*scale*rot(iTime))*vec2(iResolution.y/iResolution.x,1.)+0.5
    )).rgb;

    //the light direction for rim lighting
    vec2 lightDir = normalize(light - max(pos,EPS));

    //a light for 3d lighting of the greenish dome
    vec3 light3 = normalize(vec3(-light*7., 1.));
    //light direction
    vec3 ld = normalize(light3-vec3(pos, 0.));
    //get the hemispherical normal
    vec3 n = hemiNormal(vec2(0.),pos);
    //our ray direction looking into the screen
    vec3 rd = vec3(0.,0.,-1.);
    //diffuse
    float diff = max(0., dot(ld,n));
    //specular
    float spec = pow(max(0., dot(reflect(-ld, n), -rd)),4.);

    //shadow circle
    col = mix(col, vec3(0.0), circle(pos-lightDir*0.1*r,r,20.));
    //rim lighting circle
    col = mix(col, silver+dot(pos,-lightDir), circle(pos, r, 1.));
    //gold outer circle
    col = mix(col, silver-tx1.x+0.2, circle(pos, r*0.97, 1.));
    //sin gradient on outer circle
    col-=SIN(pos,r)/4.;
    //black inner circle
    col = mix(col, vec3(0.1), circle(pos, r*0.67, 5.));

    //rotating posision so that every disk's greenish rexture and color
    //are rotated randomly about it's center
    pos *= rot(iTime+float(ball_id));
    //the greenish yellowish color
    vec3 truCol = mix(vec3(0.45,0.32,0.1)+tx2*0.6,vec3(0.1,0.5,0.4),0.5-pos.y*8.);
    //a circle with the radius of the dome
    float inner_C = circle(pos,r*0.6, 1.);
    //get a blurred circle shape for the lighting on hit
    float flash = smoothstep(r*0.6+0.02, r*0.6, length(pos));
    //add the color for the inner dome
    col = mix(col, truCol+4.*pow(length(pos)-r*0.7, 1.), inner_C); 
   //add the flash with a 
    //glowy color based on the animation scaled from 0 to 0.5
    col += (0.5-abs(animation-0.5))*flash*vec3(0.59,0.3,0.9)*4.;
    //add the diffuse and specular lighting to the color
     col += inner_C*(clamp(diff,0.,1.)*vec3(0.45,0.32,0.1)*5. + clamp(spec,0.,1.)*vec3(0.1,0.5,0.4)*1000.);
    return col;

}


//a modified truchet pattern function so that
//I can make dashed lines if cut = 1. instead of solid 
float truchet(vec2 st,float edge, float cut){
    float d;

    st*=16.;
    vec2 stFL = floor(st);
    vec2 stFR = fract(st)-0.5;
    float id = floor(noise(stFL)*4.)*0.25;

    float spacing = 0.5;
    float width = 0.45;
    stFR *= rot(id*PI);

    vec2 pos = stFR-spacing;

    d = abs(length(pos)-spacing) + 
            smoothstep(0.4,0.5,
            abs(fract(16.*(atan(pos.y,pos.x)+PI)/PI)-0.5))*cut;
    //only cut if cut == 1
    pos = stFR+spacing;
    d = min(d,abs(length(pos)-spacing) + 
            smoothstep(0.4,0.5,
            abs(fract(16.*(atan(pos.y,pos.x)+PI)/PI)-0.5))*cut
            );

    return smoothstep(edge,edge-0.07,d+0.1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = UVn1;//this is a function in common tab
    //vignett here so I can use it to shade the truchets
    vec2 st = fragCoord/iResolution.xy;
    st *=  1.0 - st.yx;
    float vig = st.x*st.y * 15.0; // multiply with sth for intensity
    vig = pow(vig, 0.25); 

    //set a soft gradient fro the background
    vec3 backColor = mix(vec3(0.1,0.0,0.0)*1.5, vec3(0.2,0.15,0.1)*1.5, vig);
    vec3 col = backColor;
    //make thin stripes/lines that look raised maybe
    col += smoothstep(0.2,0.08,abs(fract(uv.x*42.)-0.5))*0.09;
    //use a truchet pattern to overwrite the lines
    col = mix(col, backColor*0.95, truchet(fragCoord.xy/iResolution.y,0.35,0.));
    //add a dotted truchet 
    col = mix(col, vec3(0.2,0.1,0.1)*0.1, truchet(fragCoord.xy/iResolution.y,0.22,1.)*0.8);

    //add in a second truchet overtop of the dotted one but thinner
    //two options
    //col = mix(col, mix(gold,vec3(0.59,0.3,0.9),vig),truchet(fragCoord.xy/iResolution.y, 0.19,1.));
    col = mix(col, backColor*0.99,truchet(fragCoord.xy/iResolution.y, 0.19,1.));
    //gamma or something 
    col = pow(col*0.3, vec3(0.5));

    //loop through all particles and if one is further than a radius*1.2
    //away we can just skip the f*cker right! 
    for(int i = 0; i < BALL_NUM; i++){
        if(length(uv-load0(vec2(float(i)+0.5,0.5)).xy) > RADIUS*1.2)continue;
        col = drawFancyCircle(col, uv, i, fragCoord, uv-load0(vec2(float(i)+0.5,0.5)).xy, RADIUS*0.98);
    }
    //more gamma or something
    col = pow(col, vec3(0.9));
    fragColor = vec4(col*vig*1.2,1.0);
}

